import React, { useEffect, useState } from 'react';

export default function QuantitySelector(props) {
    const [quantity, setQuantity] = useState(1);
    function increment() {
        quantity < 100 ? setQuantity(quantity + 1) : setQuantity(100);
    }
    function decrement() {
        quantity > 1 ? setQuantity(quantity - 1) : setQuantity(1);
    }
    return (
        <div className="input-group text-center">
            <button type="button" className="btn btn-danger btn-sm btn-outline-secondary" onClick={(e) => decrement()}>-</button>
            <input
                className={"input-number"}
                disabled="disabled"
                type="text"
                value={quantity}
                size="2"
                min="1"
                max="10" />
            <button type="button" className="btn btn-success btn-sm btn-outline-secondary" onClick={(e) => increment()}>+</button>

        </div>
    );
}
