import React, { useEffect, useRef, useState } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate} from 'react-router-dom'
import axios from 'axios';
import ShowMenu from './ShowMenu';
import ShowMenuItem from './ShowMenuItem';
import RestaurantsList from './RestaurantsList';
import Cart from './Cart';
import Admin from './Admin';

export const MenuContext = React.createContext();

export function App(props) {
	const [cart, setCart] = useState([]);
	const [restaurant, setRestaurant] = useState(-1);
	const [restaurantName, setRestaurantName] = useState("");
	const [menuItem, setMenuItem] = useState(-1);

	function roundedToFixed(input, digits) {
		var rounder = Math.pow(10, digits);
		var result = (Math.round(input * rounder) / rounder).toFixed(digits);
		return result > 50 ? '--' : result;
	}
	function servicesForm() {
		return (
			<BrowserRouter>
				<Link to="/">
					<button className="removebutton">
						<span className="logofont2">RivCo</span>
						<span className="logofont">DELIVERY</span>
					</button>
				</Link>
				<Link to="/cart">
					<button className="btn btn-secondary btn-lg m-1">
						<i className="bi bi-cart"></i> Cart
                    </button>
				</Link>
				<div className="mx-auto p-3 blackborder text-center">
					<div className="text-start">
						<Routes>
							<Route
								path='*'
								exact={true}
								element={
									<RestaurantsList
										setRestaurantName={setRestaurantName}
										roundedToFixed={roundedToFixed}/>
								} />
							<Route
								path={"/showMenu"}
								element={
									<ShowMenu
										restaurantName={restaurantName}
										restaurant={restaurant} />
								} />
							<Route
								path={"/showMenuItem"}
								element={
									<ShowMenuItem
										restaurant={restaurant}
										restaurantName={restaurantName}
										menuItem={menuItem}
										cart={cart}
										setCart={setCart} />
								} />
							<Route
								path={"/cart"}
								element={
									<Cart
										roundedToFixed={roundedToFixed}
										cart={cart}
										setCart={setCart} />
								} />
							<Route
								path={"/admin"}
								element={
									<Admin />
								} />
						</Routes>
					</div>
				</div>
			</BrowserRouter>
		);
	}
	return (
		<div>
			<MenuContext.Provider value={
				[
					restaurant,
					setRestaurant,
					menuItem,
					setMenuItem
				]
			}>
				<br />
				<div>{servicesForm()}</div>
				<br />
			</MenuContext.Provider>
		</div>
	);
}
export default App;
