import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import ShowMenuItem from './ShowMenuItem';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { MenuContext } from './App'

export default function ShowMenu(props) {
    const [menuItem, setMenuItem] = useContext(MenuContext);
    const [menu, setMenu] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu', { params: { restaurant: props.restaurant } }).then(res => {
            console.log(res.data);
            setMenu(res.data);
        });
    }, []);

    return (
        <div>
            <Link to="/">Back</Link><br />
            {
                menu.map((m, key) =>
                    <Link key={key} to="/showMenuItem">
                        <button onClick={(e) => setMenuItem(key + 1)} className="btn btn-secondary m-1" >{key} {m.name}</button>
                    </Link>
                )
            }
        </div>
    )
}
