import React, { useEffect, useState } from 'react';
import axios from 'axios';
import qs from 'qs';

export default function Admin2(props) {
    const [arr, setArr] = useState([]);
    //const [restaurant, setRestaurant] = useState(3);
    function submitUpdateMenuItem(e) {
        console.log(99999)
        const form = e.target;
        const inputs = {
            mid: form.elements['mid'].value,
            mi_name: form.elements['mi_name'].value,
            price: form.elements['price'].value,
        };
        console.log(inputs)
        dbPost(e, form, inputs, "updateMenuItem");
    }
    function dbPost(e, form, inputs, route) {
        e.preventDefault(e);
        console.log("submit " + Object.values(form));
        const url = "http://localhost:3000/api/" + route;
        const options = {
            method: 'POST',
            headers: {
                'content-type':
                    'application/x-www-form-urlencoded'
            },
            data: qs.stringify(inputs),
            url,
        };
        axios(options);
        console.log(qs.stringify(inputs));
    }
    useEffect(() => {

        axios.get('http://localhost:3000/api/menu/item', { params: { restaurant: restaurant } }).then(res => {
            console.log(res.data);
            setArr(res.data);
        });
    }, [restaurant, setRestaurant]);
    return(
    <form onSubmit={(e) => submitUpdateMenuItem(e)}>
        {
            arr.map((mi, key) => {
                return (
                    <div className="row m-3" key={key}>
                        <h2>Update Menu Item</h2>
                        <div className="col-sm-4">
                            <label>ID: </label>
                            <input
                                name="mid"
                                placeholder={mi.id}
                                className="form-control"
                                type="number" />
                            <label>Name: </label>
                            <input
                                name="mi_name"
                                placeholder={mi.name}
                                className="form-control"
                                type="text" />
                        </div>
                        <div className="col-sm-4">
                            <label>Price: </label>
                            <input
                                name="price"
                                placeholder={mi.price}
                                className="form-control"
                                type="text" />
                        </div>
                        <div className="col-sm-4">
                            <br />
                            <input
                                type="submit"
                                className="btn btn-primary form-control" />
                        </div>
                    </div>
                );
            })
        }
    </form>)
}
