import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { MenuContext } from './App'
/* eslint-disable */

export default function RestaurantsList(props) {
    const { setRestaurant } = useContext(MenuContext);
    // [restaurant, setRestaurant] = useState(2);
    const [restaurants, setRestaurants] = useState([]);
    useEffect(() => {
        /*axios.get('http://localhost:3000/api/menu/item', { params: { menuItem: props.menuItem } })
            .then(res => {
                console.log(res.data);
                setItemConfig(res.data);
            });*/
        const r = [{
            name: "Burger King",
            fee: "$0.50",
            miles: 2,
        },
        {
            name: "Taco Bell",
            fee: "$0.80",
            miles: 7,
        },
        {
            name: "Dominos",
            fee: "$0.20",
            miles: 1,
        }];
        setRestaurants(r);
    }, []);
    /*submit = (key) => {
        e.preventDefault();
        console.log("submit2")
        const data = [this.state.val1, this.state.val2, this.state.val3];
        console.log(data)
        const url = "http://localhost:3000/api/register";
        const options = {
            method: 'POST',
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify(data),
            url,
        };
        axios(options);
        this.setState({ restaurant: key + 1 });
        setRestaurant(key + 1);
    }*/

    return (
        <div>
            {restaurants.map((data, key) => {
                return (
                    <Link key={key} to="/showMenu">
                        <button className="btn btn-primary m-1" onClick={(e) => setRestaurant(key + 1)}>
                            <div>
                                <h6>{data.name}</h6>
                                <br />
                                <small>{data.fee} Delivery Fee</small>
                                <br />
                                <small>{data.miles} Miles</small>
                            </div>
                        </button>
                    </Link>
                );
            })}
        </div>
    );
}
