import React from 'react';
import axios from 'axios';
import { ShowMenuItem } from './ShowMenuItem';

export class ShowMenu extends React.Component {
    constructor(props) {
        super();
        this.state = {
            menu: [],

        }
        axios.get('http://localhost:3000/api/menu', { params: { restaurant: props.restaurant } }).then(res => {
            console.log(res.data)
            this.setState({menu: res.data})
        })
    }
    submit = () => {
        <ShowMenuItem />
    }
    render() {
        return (
            <div>{this.state.menu.map(m =>
                <button onClick={(e) => this.submit()}>
                    <p>{m.name}</p>
                </button>)}
            </div>)
    }
}
