import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { MenuContext } from './App'
/* eslint-disable */

export default function RestaurantsList(props) {
    const [restaurant, setRestaurant] = useContext(MenuContext);
    const [restaurants, setRestaurants] = useState([]);
    const [latitude, setLatitude] = useContext(MenuContext);
    const [longitude, setLongitude] = useContext(MenuContext);
    useEffect(() => {
        axios.get('http://localhost:3000/api/restaurants')
            .then(res => {
                console.log(res.data);
                setRestaurants(res.data);
            });
    }, []);
    function haversine_distance(lat, lng) {
        var R = 3958.8; // Radius of the Earth in miles
        var rlat1 = latitude * (Math.PI / 180); // Convert degrees to radians
        var rlat2 = lat * (Math.PI / 180); // Convert degrees to radians
        var difflat = rlat2 - rlat1; // Radian difference (latitudes)
        var difflon = (lng - longitude) * (Math.PI / 180); // Radian difference (longitudes)

        var d = 2 * R * Math.asin(Math.sqrt(Math.sin(difflat / 2) * Math.sin(difflat / 2) + Math.cos(rlat1) * Math.cos(rlat2) * Math.sin(difflon / 2) * Math.sin(difflon / 2)));
        return d;
    }
    return (
        <div>
            {restaurants.map((data, key) => {
                return (
                    <Link key={key} to={"/showMenu"}>
                        <button className="btn btn-primary m-1" onClick={(e) => setRestaurant(key + 1)}>
                            <div>
                                <b>{data.name}</b>
                                <br />
                                <small>{data.fee} Delivery Fee</small>
                                <br />
                                <small>{haversine_distance(data.lat, data.lng)} Miles</small>
                            </div>
                        </button>
                    </Link>
                );
            })}
        </div>
    );
}
