import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

export class App extends React.Component {
	constructor() {
		super();
		this.state = {
			sent: true,

		};
	}
	render() {
		return (this.state.sent ? this.servicesForm() : this.b());
	}
	submit(e) {
		e.preventDefault();
		console.log("submit2")
		const data = e.target;
		const url = "http://localhost:3000/api/register";

		const form = new FormData();
		form.append('my_field', 1);
		form.append('my_other_field', 2);
		axios.post(url, form)
			.then(res => console.log(form))
			.catch(err => console.log(err.form))
		this.setState({ sent: false });
	}
	servicesForm() {
		return (
		<form action="/api/register" method="POST"
		 onSubmit={(e) => this.submit(e)}>
			<input name="services" type="checkbox" />
			<label>Service #1</label>
			<br />
			<input name="services" type="checkbox" />
            <label>Service #2</label>
            <br />
			<input name="services" type="checkbox" />
            <label>Service #3</label>
			<br />
			<input className="btn btn-primary" type="submit"  />
			<br />
		</form>);
	}
	b() {
		return <div>Service request submitted.</div>;
	}
}
export default App;
