import React, { useEffect, useState, useContext } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom'
import axios from 'axios';
import qs from 'qs';
import QuantitySelector from './QuantitySelector';
import { CartContext } from './App'
/* eslint-disable */

export default function ShowMenuItem(props) {
    const restaurant = props.restaurant;
    const restaurantName = props.restaurantName;
    const cart = props.cart;
    const setCart = props.setCart;
    const [itemConfig, setItemConfig] = useState([]);
    const [itemIngredients, setItemIngredients] = useState([]);
    const [val1, setVal1] = useState(1);
    const [val2, setVal2] = useState(0);
    const [val3, setVal3] = useState(0);
    const [val4, setVal4] = useState(0);
    const [ingredients, setIngredients] = useState([]);
    const [type, setType] = useState("");
    const [quantity, setQuantity] = useState(1);
    const cartAmount = props.cartAmount;
    const setCartAmount = props.setCartAmount;
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            //(res.data);
            setItemConfig(res.data);
        });
        axios.get('http://localhost:3000/api/menu/item/ingredients',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            //console.log(res.data);
            setItemIngredients(res.data);
        });
    }, [cart, setCart]);

    function addToCart(e, key, item) {
        e.preventDefault();
        let boxes = [];
        const form = e.target;
        const q = form.elements['ingredients'];
        for (let i = 0; i < q.length; i++) {
            const v = q[i].checked;
            console.log(v); 
            boxes.push(v);  
        }
        const cartItem = {
            name: item.name,
            size1: item.size1,
            val1: val1,
            size2: item.size2,
            val2: val2,
            size3: item.size3,
            val3: val3,
            size4: item.size4,
            val4: val4,
            ingredients: boxes,
            arrs: arrs,
            quantity: quantity,
            price: item.price,
        };
        console.log(cartItem)
        const url = "http://localhost:3000/api/submitOrder";
        const options = {
            method: 'POST',
            headers: {
                'content-type':
                    'application/x-www-form-urlencoded'
            },
            data: qs.stringify(cartItem),
            url,
        };
        axios(options);
        console.log("item added to cart " + item.name);
        cart.push(cartItem);
        setCart(
            cart.map((m) => {
                return m
            })
        );
        navigate("/showMenu");
    }
    function changeRadio1() {
        setVal1(1);
        setVal2(0);
        setVal3(0);
        setVal4(0);
    }

    function changeRadio2() {
        setVal1(0);
        setVal2(1);
        setVal3(0);
        setVal4(0);
    }

    function changeRadio3() {
        setVal1(0);
        setVal2(0);
        setVal3(1);
        setVal4(0);
    }
    function changeRadio4() {
        setVal1(0);
        setVal2(0);
        setVal3(0);
        setVal4(1);
    }
    const arrs = [];
    const titles = [];
    const ingreds = [];
    function kjk() {
        console.log(JSON.stringify(result));
        for (const j in result) {
            for (const i in result[j]) {
                //console.log("jnjnn " + result[j][i]['ingredients_name']);
                console.log(result[j][i])
                ingreds.push(result[j][i]);
            }
        }
        /*for (const mm in result) {
            const ar = Array.from(Object.values(result[mm]));
            titles.push(mm);
            arrs.push(ar.map((v) => { 
                return  v;
            }));
        }*/
    }
    const result = Object.groupBy(itemIngredients, i => i.type);
    const [lastCategory, setLastCategory] = "";
    //console.log(result);
    return (
    <div>
        <Link to="/showMenu">
            <button className="btn btn-secondary btn-lg m-1">
                <i className="bi bi-arrow-return-left"></i> {restaurantName != undefined ? restaurantName : "Back"}
            </button>
        </Link>
        {
           itemConfig.map((item, key) =>
                <form
                    key={key}
                    className="form-check"
                    onSubmit={(e) => addToCart(e, key, item)}>
                    <div className="row" key={key}>
                        <div className="col-sm-12 text-center">
                            <h2>{item.name}</h2>
                            <h3>
                                <b className="text-success">${item.price}</b>
                            </h3>
                            <QuantitySelector
                                inputValue={quantity}
                                onInputValueChange={setQuantity} />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-6">
                            <div className="p-3 m-1 blackborder">
                                <h5>Size</h5>
                                {(item.size1 != null && item.size1 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                required
                                                type="radio"
                                                checked={val1}
                                                onChange={(e) => changeRadio1()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size1}
                                        </label>
                                    </div> : ""
                                }
                                {(item.size2 != null && item.size2 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                type="radio"
                                                checked={val2}
                                                onChange={(e) => changeRadio2()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size2}
                                        </label>
                                    </div> : ""
                                }
                                {(item.size3 != null && item.size3 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                type="radio"
                                                checked={val3}
                                                onChange={(e) => changeRadio3()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size3}
                                        </label>
                                    </div> : ""
                                }
                                {(item.size4 != null && item.size4 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                type="radio"
                                                checked={val4}
                                                onChange={(e) => changeRadio4()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size4}
                                        </label>
                                    </div> : ""
                                }
                            </div>
                        </div>
                        <div className="col-sm-6">
                            {
                                kjk()
                            }
                            {
                                ingreds.map((q) => {
                                    return (
                                        <div>
                                            {lastCategory != q['type'] ? <h3>{q['type']}</h3> : ""}
                                            {setLastCategory(q['type'])}
                                            <div className="p-3 m-1 blackborder">
                                                {q['inputType'] == 0 ? 
                                                (<label>
                                                    <input
                                                        type="checkbox" 
                                                        name={"ingredients"} />
                                                    <span> </span>
                                                    <span>{q['ingredients_name']}</span>
                                                </label>)
                                                : 
                                                (<label>
                                                    <input
                                                        type="radio"
                                                        name="ingredients" />
                                                    <span> </span>
                                                    <span>{q['ingredients_name']}</span>
                                                </label>)}
                                            </div>
                                        </div>
                                    );
                                })
                            }
                            <input
                                type="submit"
                                className="btn btn-primary mt-1 form-control"
                                value={"Add " + quantity + " to cart"} />
                        </div>
                    </div>
                </form>
            )
        }
    </div>);
}
