	const [cart, setCart] = useState([]);
	const [restaurant, setRestaurant] = useState(-1);
	const [menuItem, setMenuItem] = useState(-1);

	function servicesForm() {
		return (
			<BrowserRouter>
				<Routes>
					<Route
						path="/"
						element=
					{
							<RestaurantsList  />
					} />
					<Route
						path={"/showMenu"}
						element=
					{
						<ShowMenu restaurant={restaurant} />
					} />
					<Route
						path={"/showMenuItem"}
						element=
					{
						<ShowMenuItem
							restaurant={restaurant}
							menuItem={menuItem} />
					} />
					<Route
						path={"/cart"}
						element={<Cart />} />
				</Routes>
			</BrowserRouter>
		);
	}

	return (
		<div>
