import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import qs from 'qs';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
/* eslint-disable */

export default function ShowMenuItem(props) {
    const [itemConfig, setItemConfig] = useState([]);
    const [val1, setVal1] = useState(0);
    const [val2, setVal2] = useState(0);
    const [val3, setVal3] = useState(0);
    const navigate = useNavigate();
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item', { params: { menuItem: props.menuItem } })
        .then(res => {
            console.log(res.data);
            setItemConfig(res.data);
        });
    }, []);
    function submit(e, key) {
        e.preventDefault();
        console.log("submit2")
        const data = [val1, val2, val3];
        console.log(data)
        const url = "http://localhost:3000/api/submitOrder";
        const options = {
            method: 'POST',
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify(data),
            url,
        };
        axios(options);
        //this.setState({ restaurant: key + 1 });
        //setRestaurant(key + 1);
        navigate("/orderStatus");
    }
    return (
    <div>
        <Link to="/showMenu">Back</Link>
        {
           itemConfig.map((m, key) =>
               <div key={key}>
                   <h3>{m.name}</h3>
                   <form key={key} className="form-check" onSubmit={(e) => submit(e, key)}>
                       {m.size1 != "" ? <label><input type="radio" checked={val1} onChange={(e) => setVal1(e.target.value)} name="itemSize" className="form-check-input" />{m.size1}</label> : ""}
                       {m.size1 != "" ? <br /> : ""}
                       {m.size2 != "" ? <label><input type="radio" checked={val2} onChange={(e) => setVal2(e.target.value)} name="itemSize" className="form-check-input" />{m.size2}</label> : ""}
                       {m.size1 != "" ? <br /> : ""}
                       {m.size3 != "" ? <label><input type="radio" checked={val3} onChange={(e) => setVal3(e.target.value)} name="itemSize" className="form-check-input" />{m.size3}</label> : ""}
                       {m.size1 != "" ? <br /> : ""}
                       <input type="submit" className="btn btn-primary" value="Add to cart" />
                    </form>
                   <p>hi</p>
                </div>
            )
        }
    </div>);
}
