            method: 'POST',
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify(data),
            url,
        };
        axios(options);
        this.setState({ restaurant: key + 1 });
        setRestaurant(key + 1);
    }
    return (
    <div>
        <Link to="/showMenu">Back</Link>
        {
           itemConfig.map((m, key) =>
               <div key={key}>
                   <h3>{m.name}</h3>
                   <form key={key} class="form-check" onSubmit={submit(key)}>
                       {m.size1 != "" ? <><input type="radio" name="itemSize" class="form-check-input" />{m.size1}</> : ""}
