        for (let i = 0; i < elements.length; i++) {
            const v = elements[i];
            boxes.push([v.checked, customizer[i].value]);
            if (halfer[i] == undefined) {
                continue;
            }
            boxes2.push([halfer[i].value]);
        }
        console.log(boxes.length);
        console.log(boxes2.length);
        const cartItem = {
            name: item.name,
            size1: item.size1,
            val1: val1,
            size2: item.size2,
            val2: val2,
            size3: item.size3,
            val3: val3,
            size4: item.size4,
            val4: val4,
            ingredients: boxes,
            arrs: ingredientsData,
            quantity: quantity,
            price: price,
            customizer: customizer,
            halfer: boxes2,
        };
        //console.log("?? "+halfer)
