                                                        t = t.add(currency(i['price']).divide(2).value);
                                                        console.log("t4: " + t.value);
                                                    } else {
                                                        t = t.add(currency(i['price']).value);
                                                    }
                                                    console.log("t3: " + t.value);
                                                }
                                            }
