                                                setLastCategoryPrinted(q['type'])
                                            }
                                            <div className="p-3 m-1 text-bg-dark rounded">
                                                <div className="row">
                                                    <div className="col-lg-6">
                                                        {
                                                            (q['inputType'] == 0 ? 
                                                                (<label>
                                                                    <input
                                                                        onChange={(e) => changeEnabledCheckbox(e)}
                                                                        type="checkbox"
