                                        inputValue={quantity}
                                        onInputValueChange={setQuantity} />
                                </div>
                                <div className="col-md-8">
                                    <input
                                        type="submit"
                                        className="btn btn-primary form-control"
                                        value={"Add " + quantity + " to cart"} />
                                </div>
                            </div>
                        </div>
                    </div>
                </form>
