import React, { useEffect, useState } from 'react';

export default function QuantitySelector(props) {
    const quantity = useState(props.inputValue);
    const setQuantity = useState(props.onInputValueChange);
    //const quantity = props.inputValue;
    //const setQuantity = props.setQuantity;
    function increment(e) {
        quantity < 100 ? setQuantity(quantity + 1) : setQuantity(100);
        quantity < 100 ? props.onInputValueChange(quantity + 1) : props.onInputValueChange(100)
        console.log("qty: " + quantity + 1)
    }
    function decrement(e) {
        quantity > 1 ? setQuantity(quantity - 1) : setQuantity(1);
        quantity > 1 ? props.onInputValueChange(quantity - 1) : props.onInputValueChange(1);
        console.log("qty: " + quantity - 1)
    }
    return (
        <span className="input-group mb-1">
            <button
                type="button"
                className="btn btn-primary btn-sm btn-outline-secondary text-white"
                onClick={(e) => decrement(e)}>
                <i class="bi bi-dash-lg"></i>
            </button>
            <input
                className={"input-number text-center"}
                disabled="disabled"
                type="text"
                value={quantity}
                size="2"
                min="1"
                max="10" />
            <button
                type="button"
                className="btn btn-primary btn-sm btn-outline-secondary text-white"
                onClick={(e) => increment(e)}><i class="bi bi-plus-lg"></i></button>
        </span>
    );
}
