import React, { useEffect, useState } from 'react';
import currency from 'currency.js';
import { CartItemDetails, calcSubtotal, Subtotal, Ingredients } from './Cart';
import { roundedToFixed } from './App';
import { DeliveryAddress, getFullAddress } from './RestaurantsList';
import { useNavigate } from 'react-router-dom';
import { dbPost2 } from './Admin';

export default function Checkout(props) {
	const [fee, setFee] = useState(0.00);
	const [subtotal, setSubtotal] = useState(0.00);
	const cart = props.cart;
	const setCart = props.setCart;
	const address = props.address;
    const setAddress = props.setAddress;
    const showGetLocation = props.showGetLocation;
	const setShowGetLocation = props.setShowGetLocation;
	const [businessType1, setBusinessType1] = useState(0);
	const [businessType2, setBusinessType2] = useState(0);
	const [businessType3, setBusinessType3] = useState(0);
	const [textAreaValue, setTextAreaValue] = useState("");
	const navigate = useNavigate();
	useEffect(() => {
		let newSubtotal = calcSubtotal(cart, setSubtotal);
		setSubtotal(newSubtotal);
	}, [cart]);

	function submit(e) {
		e.preventDefault();
		const addr = getFullAddress(address);
		console.log("submitted " 
			+ addr + " " 
			+ textAreaValue + " " 
			+ businessType1 + " " 
			+ businessType2 + " " 
			+ businessType3);
		let bt = "";
		if (businessType1 == 1) {
			bt = "Home";
		} else if (businessType2 == 1) {
			bt = "Apartment";
		} else if (businessType3 == 1) {
			bt = "Business";
		} 
		const data2 = [
			addr, 
			textAreaValue, 
			bt
		];
		let arr = [];
		//dbPost2(e, data2, "co2");
		for (let i = 0; i < cart.length; i++) {
			console.log(cart[i].name);
			let size = "";
			if (cart[i].val1 == 1) {
				size = cart[i].size1;
			} else if (cart[i].val2 == 1) {
				size = cart[i].size2;
			} else if (cart[i].val3 == 1) {
				size = cart[i].size3;
			} else if (cart[i].val4 == 1) {
				size = cart[i].size4;
			}
			console.log(size);
			console.log(Ingredients(cart[i]));
			console.log(cart[i].price);
			console.log(cart[i].quantity);
			const data = [
				size, 
				Ingredients(cart[i]), 
				cart[i].price, 
				cart[i].quantity
			];
			arr.push(data);
		}
		dbPost2(e, [data2, arr], "co");
		// end 
		setCart([]);
		navigate("/success");
	}
	function setHome() {
		setBusinessType1(true)
		setBusinessType2(false)
		setBusinessType3(false)
	}
	function setApartment() {
		setBusinessType1(false)
		setBusinessType2(true)
		setBusinessType3(false)
	}
	function setBusiness() {
		setBusinessType1(false)
		setBusinessType2(false)
		setBusinessType3(true)
	}
	return (cart.length > 0 ? (
		<form onSubmit={(e) => submit(e)}>
			<h1>Checkout</h1>
			<DeliveryAddress 
				showGetLocation={showGetLocation} 
				setShowGetLocation={setShowGetLocation} 
				address={address} 
			/>
			<div className="row">
				<DeliveryInstructions 
					setTextAreaValue={setTextAreaValue} 
				/>
				<BusinessTypes
					cart={cart}
					subtotal={props.subtotal}
					fee={props.fee}
					businessType1={props.businessType1}
					setHome={props.setHome}
					businessType2={props.businessType2}
					setApartment ={props.setApartment}
					businessType3 = {props.businessType3}
					setBusiness = {props.setBusiness}
				/>
			</div>
			<OrderReview />
		</form>) : <h1>Error: You cannot view this page right now.</h1>
	);
}

export function OrderReview(props) {
	const cart = props.cart;
	const subtotal = props.subtotal;
	 const fee = props.fee;
	return (
		<div className="row">
			<div className="col-md-12 text-center p-1">
				<h3>Order Review</h3>
			</div>
			<div className="col-md-7">
			{
				cart.map(ci => {
					return (
						<div className="cartitem p-3">
							<CartItemDetails cartItem={ci} />	
						</div>
					)
				})
			}
			</div>
			<div className="col-md-5">
				<Subtotal subtotal={subtotal} />
				<h3 className="text-end">
					Service Fee: <b>${roundedToFixed(currency(fee), 2)}</b>
				</h3>
				<h3 className="text-end">
					Total: <b>${roundedToFixed(currency(subtotal).add(currency(fee)).value, 2)}</b>
				</h3>
				<button className="btn btn-primary form-control">Deliver now</button>
			</div>
		</div>
	)
}

export function BusinessTypes(props) {

  	const businessType1 = props.businessType1;
  	const setHome = props.setHome;
  	const businessType2 = props.businessType2
  	const setApartment = props.setApartment;
  	const businessType3 = props.businessType3;
  	const setBusiness = props.setBusiness;
	return (
		<div className="col-md-5">
			<div className="text-bg-dark p-3 m-1 rounded">
				<label>
					<input 
						required 
						checked={businessType1} 
						onChange={(e) => setHome()} 
						name="deliverytype" 
						type="radio" 
					/> Home
				</label>
			</div>
			<div className="text-bg-dark p-3 m-1 rounded">
				<label>
					<input 
						required 
						checked={businessType2}
						onChange={(e) => setApartment()} 
						name="deliverytype" 
						type="radio"
					/> Apartment
				</label>
			</div>
			<div className="text-bg-dark p-3 m-1 rounded">
				<label>
					<input 
						required 
						checked={businessType3}
						onChange={(e) => setBusiness()} 
						name="deliverytype"
						type="radio"
					/> Business
				</label>
			</div>
		</div>
	)
}

export function DeliveryInstructions(props) {
	const setTextAreaValue = props.setTextAreaValue;
	return (
		<div className="col-md-7">
			<div className="text-bg-dark p-3 m-1 rounded">
				<h4>Delivery Instructions: </h4>
				<textarea
					onChange={(e) => setTextAreaValue(e.target.value)}
					required
					rows="2" 
					placeholder="Enter delivery instructions here..." 
					className="form-control" 
				/>
			</div>
		</div>
	)
}
