import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ShowMenuItem from './ShowMenuItem';

export class ShowMenu extends React.Component {
    constructor(props) {
        super();
        this.state = {
            menu: [],
            itemSelected: false,
        }
        axios.get('http://localhost:3000/api/menu', { params: { restaurant: props.restaurant } }).then(res => {
            console.log(res.data)
            this.setState({menu: res.data})
        })
    }
    submit = () => {
        this.setState({ itemSelected: true });
    }



    render() {
        return (
            <div>{!this.state.itemSelected ? this.state.menu.map((m, key) =>
                <button className="btn btn-primary m-1" onClick={(e) => this.submit()}>
                    <p>{m.name}</p>
                </button>) : <ShowMenuItem menuItem={1} />}
            </div>)
    }

}
