import React, { useEffect, useRef, useState } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import axios from 'axios';
import ShowMenu from './ShowMenu';
import ShowMenuItem from './ShowMenuItem';
import RestaurantsList from './RestaurantsList';
import Cart from './Cart';

export const MenuContext = React.createContext();

export function App(props) {
	const [cart, setCart] = useState([]);
	const [restaurant, setRestaurant] = useState(-1);
	const [menuItem, setMenuItem] = useState(-1);

	function roundedToFixed(input, digits) {
		var rounder = Math.pow(10, digits);
		var result = (Math.round(input * rounder) / rounder).toFixed(digits);
		return result > 50 ? '--' : result;
	}

	function servicesForm() {
		return (
			<div
				className="mx-auto p-1 blackborder">
				<BrowserRouter>
					<Routes>
						<Route
							path="/"
							element={
								<RestaurantsList
									roundedToFixed={roundedToFixed}/>
							} />
						<Route
							path={"/showMenu"}
							element={
								<ShowMenu restaurant={restaurant} />
							} />
						<Route
							path={"/showMenuItem"}
							element={
								<ShowMenuItem
									restaurant={restaurant}
									menuItem={menuItem}
									cart={cart}
									setCart={setCart} />
							} />
						<Route
							path={"/cart"}
							element={
								<Cart
									roundedToFixed={roundedToFixed}
									cart={cart}
									setCart={setCart} />
							} />
					</Routes>
				</BrowserRouter>
			</div>
		);
	}
	return (
		<div>
			<MenuContext.Provider value={
				[
					restaurant,
					setRestaurant,
					menuItem,
					setMenuItem
				]
			}>
				<br />
				{servicesForm()}
				<br />
			</MenuContext.Provider>
		</div>
	);
}
export default App;
