import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ShowMenuItem from './ShowMenuItem';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'

export class ShowMenu extends React.Component {
    constructor(props) {
        super();
        this.state = {
            menu: [],
            itemSelected: false,
        }
        axios.get('http://localhost:3000/api/menu', { params: { restaurant: props.restaurant } }).then(res => {
            console.log(res.data)
            this.setState({menu: res.data})
        })
    }

    submit = () => {
        this.setState({ itemSelected: true });
    }

    render() {
        return (
        <BrowserRouter>
            <div>
            {
                this.state.menu.map((m, key) =>
                    <Link key={key}  to="/showMenuItem">{m.name}</Link>
                )
            }
            </div>
            <Routes>
                <Route path="/showMenuItem" element={<ShowMenuItem menuItem={1} />} />
            </Routes>
        </BrowserRouter>)
    }

}
