import React, { useEffect, useState } from 'react';
import currency from 'currency.js';
import { printCartItemDetails, calcSubtotal, printSubtotal } from './Cart';
import { roundedToFixed } from './App';

export default function Checkout(props) {
		const [fee, setFee] = useState(0.00);
		const [subtotal, setSubtotal] = useState(0.00);
		const cart = props.cart;
		useEffect(() => {
			let newSubtotal = calcSubtotal(cart, setSubtotal);
			setSubtotal(newSubtotal);
		}, [cart]);
		return (
			<div>
				<h1>Checkout</h1>
				<div className="row">
					<div className="col-md-7">

						<div className="text-bg-dark p-3 m-1 rounded">
							<h4>Delivery Instructions: </h4>
							<textarea rows="4" placeholder="Enter delivery instructions here..." className="form-control" />

						</div>
					</div>
					<div className="col-md-5">
						<div className="text-bg-dark p-3 m-1 rounded"><label><input name="deliverytype" type="radio" /> Home</label></div>
						<div className="text-bg-dark p-3 m-1 rounded"><label><input name="deliverytype" type="radio" /> Apartment</label></div>
						<div className="text-bg-dark p-3 m-1 rounded"><label><input name="deliverytype"type="radio" /> Business</label></div>
						<div className="row text-bg-dark p-3 m-1 rounded">
							<div className="col-lg-6">
								<h4>Deliver by: </h4>

							</div>
							<div className="col-lg-6 text-end">
								<input type="time" id="appt" name="appt" min="09:00" max="18:00" required />
							</div>
						</div>
					</div>
				</div>
				<div className="row">
					<div className="col-md-7">
					{
						cart.map(ci => {
							return (
								<div className="cartitem p-3">
									{printCartItemDetails(ci)}	
								</div>
							)
						})
					}
					</div>
					<div className="col-md-5">
						{printSubtotal(subtotal)}
						<h3 className="text-end">
							Service Fee: <b>${roundedToFixed(fee, 2)}</b>
						</h3>
						<h3 className="text-end">
							Total: <b>${roundedToFixed(currency(subtotal).add(currency(fee)).value, 2)}</b>
						</h3>
					</div>
				</div>
			</div>
		);
	}
