import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'
import { roundedToFixed } from './App';

export default function Cart(props) {
    const cart = props.cart;
    const setCart = props.setCart;
    const [subtotal, setSubtotal] = useState(0.00);
    const calcSubtotal = (c) => {
        let totalAmount = 0;
        for (const item in c) {
            totalAmount += c[item].quantity * c[item].price;
        }
        console.log(totalAmount);
        setSubtotal(totalAmount);
        return roundedToFixed(totalAmount, 2);
    };

    function removeFromCart(e, cid) {
        console.log(cart.length);
        let newCart = cart.filter((cartItem, k) => {
            return k !== cid;
        });
        console.log("rm from cart - newCart: " + newCart.length);
        setCart(newCart);
        console.log("rm from cart - cart: " + cart.length);
        calcSubtotal(cart);
    }

    useEffect(() => {
        let newSubtotal = calcSubtotal(cart);
        setSubtotal(newSubtotal);
    }, [cart]);

    function recalc(e, cid) {
        removeFromCart(e, cid);
        console.log("recalc - cart size: " + cart.length);
    }

    function decrement(e, key) {
        setCart(cart => {
            const minQuantity = 2;
            return cart.map((cartItem, i) => {
                if (cartItem.quantity < minQuantity)
                    return cartItem;
                if (key === i) {
                    return { ...cartItem, quantity: cartItem.quantity - 1 }
                } else {
                    return cartItem;
                }
            })
        });
    }

    function increment(e, key) {
        setCart(cart => {
            const maxQuantity = 20;
            return cart.map((cartItem, i) => {
                if (key === i) {
                    if (cartItem.quantity >= maxQuantity) {
                        return { ...cartItem, quantity: maxQuantity }
                    }
                    return { ...cartItem, quantity: cartItem.quantity + 1 }
                } else {
                    return cartItem;
                }
            })
        });
    }

    function printIngredients(cartItem) {
        let ingredients = cartItem.ingredients;
        let halfer = cartItem.halfer;
        let arrs = cartItem.arrs;
        let str = "";
        console.log(JSON.stringify(ingredients))
        for (let i = 0; i < ingredients.length; i++) {
            let h = halfer[i] != undefined ? halfer[i].value : "";
            let itemWithOptions = arrs[i]['customize'] ? " ("+ ingredients[i][1] + ", " + halfer[i][1] + ")" : "";
            str += ingredients[i][0] ? (arrs[i]['ingredients_name'] + (itemWithOptions) + ", ")  : "";
            console.log(JSON.stringify(ingredients[i][1]))
        }
        str = str.replace(/,\s*$/, "");
        return str;
    }

    function printCart(cart) {
        return (cart.map((cartItem, key) => {
            return (
                <div
                    className="cartitem p-3"
                    key={key}>
                    { cart.length === 0 ? "Empty." : ""}
                    <div className="">
                        <h5>{cartItem.name}</h5>
                        <b className="text-success">
                            ${cartItem.price} x {cartItem.quantity} = ${roundedToFixed(cartItem.price * cartItem.quantity, 2)}
                        </b>
                        <br />
                        <span>{cartItem.val1 == 1 ? cartItem.size1 : ""}</span>
                        <span>{cartItem.val2 == 1 ? cartItem.size2 : ""}</span>
                        <span>{cartItem.val3 == 1 ? cartItem.size3 : ""}</span>
                        <span>{cartItem.val4 == 1 ? cartItem.size4 : ""}</span>
                        <br />
                        <small>
                            {printIngredients(cartItem)}
                        </small>
                        <div className="row text-sm-end">
                            <div className="col-sm-6">
                                <span className="input-group mb-1">
                                    <button
                                        type="button"
                                        className="btn btn-primary btn-sm btn-outline-secondary text-white"
                                        onClick={(e) => decrement(e, key)}>
                                        <i className="bi bi-dash-lg"></i>
                                    </button>
                                    <input
                                        className={"input-number text-center"}
                                        disabled="disabled"
                                        type="textparse"
                                        value={cartItem.quantity}
                                        size="2" />
                                    <button
                                        type="button"
                                        className="btn btn-primary btn-sm btn-outline-secondary text-white"
                                        onClick={(e) => increment(e, key)}>
                                        <i className="bi bi-plus-lg"></i>
                                    </button>
                                </span>
                            </div>
                            <div className="col-sm-6 float-sm-right">
                                <button
                                    className="btn btn-danger btn-sm"
                                    onClick={(e) => recalc(e, key)}>Remove
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }))
    }
    return (
        <div className="m-1">
            <Link to="/showMenu">
                <button className="btn btn-secondary btn-lg">
                    <i className="bi bi-arrow-return-left"></i> Back
                </button>
            </Link>
            <h1>Shopping Cart</h1>
            <div className="row">
                <div className="col-sm-7">
                    {printCart(cart)}
                </div>
                <div className="col-sm-5">
                <h3>Subtotal: <b className="text-success">
                        ${(subtotal)}</b>
                    </h3>
                </div>
            </div>
        </div>
    );
}
