import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import ShowMenu from './ShowMenu';
import ShowMenuItem from './ShowMenuItem';
import RestaurantsList from './RestaurantsList';
import Cart from './Cart';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'

export const MenuContext = React.createContext();

export const CartContext = React.createContext();

const apiKey = 'AIzaSyAyg7Hqr5wUTTv2uL6PMA3-F7fcHBGzhOw';
const mapApiJs = 'https://maps.googleapis.com/maps/api/js';
const geocodeJson = 'https://maps.googleapis.com/maps/api/geocode/json';


// load google map api js

function loadAsyncScript(src) {
	return new Promise(resolve => {
		const script = document.createElement("script");
		Object.assign(script, {
			type: "text/javascript",
			async: true,
			src
		})
		script.addEventListener("load", () => resolve(script));
		document.head.appendChild(script);
	})
}

const extractAddress = (place) => {

	const address = {
		city: "",
		state: "",
		zip: "",
		country: "",
		plain() {
			const city = this.city ? this.city + ", " : "";
			const zip = this.zip ? this.zip + ", " : "";
			const state = this.state ? this.state + ", " : "";
			return city + zip + state + this.country;
		}
	}

	if (!Array.isArray(place?.address_components)) {
		return address;
	}

	place.address_components.forEach(component => {
		const types = component.types;
		const value = component.long_name;

		if (types.includes("locality")) {
			address.city = value;
		}

		if (types.includes("administrative_area_level_2")) {
			address.state = value;
		}

		if (types.includes("postal_code")) {
			address.zip = value;
		}

		if (types.includes("country")) {
			address.country = value;
		}

	});

	return address;
}


export function App(props) {
	const [debug, setDebug] = useState(false);
	const defaultAddress = function () {return <span className='text-danger'>Address Required!</span>};
	//const [address, setAddress] = useState(defaultAddress);
	const [showGetLocation, setShowGetLocation] = useState(true);
	const [cart, setCart] = useState([]);
	const [restaurant, setRestaurant] = useState(-1);
	const [menuItem, setMenuItem] = useState(-1);
	function servicesForm() {
		return (

			<BrowserRouter>
				<div>
					<h6>Deliver to: <i>{//address
					}</i>
						{showGetLocation ?
							<button
								className="btn btn-secondary btn-sm m-1"
								onClick={(e) => getAddress()}>Get My Location
							</button> : ""
						}
					</h6>
				</div>
				<Routes>
					<Route path="/" element={<RestaurantsList />} />
					<Route path={"/showMenu"} element={<ShowMenu restaurant={restaurant} />} />
					<Route path={"/showMenuItem"} element={<ShowMenuItem restaurant={restaurant} menuItem={menuItem} />} />

					<Route path={"/cart"} element={<Cart />} />
				</Routes>
			</BrowserRouter>
		);
	}
	const [latitude, setLatitude] = useState();
	const [longitude, setLongitude] = useState();
	useEffect(() => {
		navigator.geolocation.getCurrentPosition((position) => {
			setLatitude(position.coords.latitude);
			setLongitude(position.coords.longitude);
		})

	})
	function getAddress() {
		fetch(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&sensor=false&key=AIzaSyAyg7Hqr5wUTTv2uL6PMA3-F7fcHBGzhOw`)
			.then(response => response.json())
			.then(data => setAddress(data.results[0].formatted_address))
			.catch(error => alert(error));
		setShowGetLocation(false);
	}
	const searchInput = useRef(null);
	const [address, setAddress] = useState({});
	// init gmap script
	const initMapScript = () => {
		// if script already loaded
		if (window.google) {
			return Promise.resolve();
		}
		const src = `${mapApiJs}?key=${apiKey}&libraries=places&v=weekly`;
		return loadAsyncScript(src);
	}

	// do something on address change
	const onChangeAddress = (autocomplete) => {
		const place = autocomplete.getPlace();
		setAddress(extractAddress(place));
	}

	// init autocomplete
	const initAutocomplete = () => {
		if (!searchInput.current) return;

		const autocomplete = new window.google.maps.places.Autocomplete(searchInput.current);
		autocomplete.setFields(["address_component", "geometry"]);
		autocomplete.addListener("place_changed", () => onChangeAddress(autocomplete));

	}


	const reverseGeocode = ({ latitude: lat, longitude: lng }) => {
		const url = `${geocodeJson}?key=${apiKey}&latlng=${lat},${lng}`;
		searchInput.current.value = "Getting your location...";
		fetch(url)
			.then(response => response.json())
			.then(location => {
				const place = location.results[0];
				const _address = extractAddress(place);
				setAddress(_address);
				searchInput.current.value = _address.plain();
			})
	}


	const findMyLocation = () => {
		if (navigator.geolocation) {
			navigator.geolocation.getCurrentPosition(position => {
				reverseGeocode(position.coords)
			})
		}
	}





	// load map script after mounted
	useEffect(() => {
		initMapScript().then(() => initAutocomplete())
	}, []);
	return (
		<>
			<div>
			<div className="search">
				<span></span>
				<input ref={searchInput} type="text" placeholder="Search location...."/>
				<button onClick={findMyLocation}></button>
			</div>

			<div className="address">
				<p>City: <span>{address.city}</span></p>
				<p>State: <span>{address.state}</span></p>
				<p>Zip: <span>{address.zip}</span></p>
				<p>Country: <span>{address.country}</span></p>
			</div>

			</div>

			{debug ? latitude : ""}
			<br />
			{debug ? longitude : ""}
			<CartContext.Provider value={[cart, setCart]}>
				<MenuContext.Provider value={[restaurant, setRestaurant, menuItem, setMenuItem]}>
					<div className="mx-auto">
						{
							servicesForm()
						}

					</div>
				</MenuContext.Provider>
			</CartContext.Provider>
		</>
	);
}
export default App;
