import React from 'react';
import axios from 'axios';
import qs from 'qs';

export class App extends React.Component {
	constructor() {
		super();
		this.state = {
			sent: true,
			val1: null,
			val2: null,
			val3: null,
		};
	}
	render() {
		return (this.state.sent ? this.servicesForm() : this.b());
	}
	submit(e) {
		e.preventDefault();
		console.log("submit2")
		const data = [this.state.val1, this.state.val2, this.state.val3];
		console.log(data)
		const url = "http://localhost:3000/api/register";
		const options = {
			method: 'POST',
			headers: { 'content-type': 'application/x-www-form-urlencoded' },
			data: qs.stringify(data),
			url,
		};
		axios(options);
		this.setState({ sent: false });
	}
	servicesForm() {
		const restaurants = [{
			name: "Pizza Hut",
			fee: "$0.50",
			miles: 2
		},
		{
			name: "Taco Bell",
			fee: "$0.80",
			miles: 7
		}];
		return (
		<form action="/api/register" method="POST" onSubmit={(e) => this.submit(e)}>
		{
			restaurants.map(function (data) {
				return (
					<button href="#">
						<div>
							{data.name}
							<br /><small>{data.fee} Delivery Fee</small>
							<br /><small>{data.miles} Miles</small>
						</div>
					</button>);
			})
		}
		</form>);
	}
	b() {
		return <div>Service request submitted.</div>;
	}
}
export default App;
