import React, { useEffect, useState } from 'react';

export default function QuantitySelector(props) {
    const [quantity, setQuantity] = useState(props.inputValue);
    function increment(e) {
        quantity < 100 ? setQuantity(quantity + 1) : setQuantity(100);
        quantity < 100 ? props.onInputValueChange(quantity + 1) : props.onInputValueChange(100)
        console.log("qty: " + quantity + 1)
    }
    function decrement(e) {
        quantity > 1 ? setQuantity(quantity - 1) : setQuantity(1);
        quantity > 1 ? props.onInputValueChange(quantity - 1) : props.onInputValueChange(1);
        console.log("qty: " + quantity - 1)
    }
    return (
        <span>
            <span className="input-group">
                <button
                    type="button"
                    className="btn btn-primary btn-sm btn-outline-secondary"
                    onClick={(e) => decrement(e)}>-</button>
                <input
                    className={"input-number"}
                    disabled="disabled"
                    type="text"
                    value={quantity}
                    size="2"
                    min="1"
                    max="10" />
                <button
                    type="button"
                    className="btn btn-primary btn-sm btn-outline-secondary"
                    onClick={(e) => increment(e)}>+</button>
            </span>
        </span>
    );
}
