import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route, Link} from 'react-router-dom'
import ShowMenu from './ShowMenu';
import ShowMenuItem from './ShowMenuItem';
import RestaurantsList from './RestaurantsList';
import Cart from './Cart';
import Admin from './Admin';
import Admin2 from './Admin2'
import Checkout from './Checkout'
import axios from 'axios';

export const MenuContext = React.createContext();

export function roundedToFixed(input, digits) {
	var rounder = Math.pow(10, digits);
	var result = (Math.round(input * rounder) / rounder).toFixed(digits);
	return result;
}

export function OrderList(props) {
	const [orders, setOrders] = useState([]);
	const [orderItems, setOrderItems] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/orders')
            .then(res => {
                console.log(res.data);
                setOrders(res.data);
            });
	}, [setOrders]);
	function getOrderItems(id) {
		console.log("oid: " + id)
		axios.get('http://localhost:3000/api/order_items',
		{ params: { oid: id } })
            .then(res => {
                console.log(res.data);
                setOrderItems(res.data);
            });
	}
    return (
        <div className="row m-1">
            {
				<div className="col-sm-6">
					{
						orders.map((o, key) => {
							return (
								<button 
									key={key}
									className="btn btn-primary m-1" 
									onClick={(e) => getOrderItems(o.id)}>
									<div key={key} className="row">
										<div className="col-sm-12">
											<small>
												{
													new Intl.DateTimeFormat('en-US', {
														dateStyle: 'short',
														timeStyle: 'short',
														timeZone: "America/Los_Angeles"
													}).format(new Date(o.date))
												}
											</small>
										</div>
										<div className="col-sm-12">
											{o.address}
										</div>
										<div className="col-sm-12">
											{o.businessType}
										</div>
										<div className="col-sm-12">
											{o.text}
										</div>

									</div>
								</button>
							)
						})
					}
				</div>
            }
			{
				<div className="col-sm-6 blackborder">
					<ol>
					{
						orderItems.map((oi, key2) => {
							return (
								<li className="cartitem m-3 p-3" key={key2}>
									<div>
										<h5>Size:</h5>
										{oi.size}
									</div>
									<div>
										<h5>Ingredients:</h5>
										{oi.ingredients}
									</div>
									<div>
										<h5>Price:</h5>
										{oi.price}
									</div>
									<div>
										<h5>Quantity:</h5>
										{oi.quantity}
									</div>
								</li>
							)
						})
					}
					</ol>
				</div>
			}
        </div>
    );
}
export function App(props) {
	const [cart, setCart] = useState([]);
	const [cartAmount, setCartAmount] = useState(0);
	const [restaurant, setRestaurant] = useState(-1);
	const [restaurantName, setRestaurantName] = useState("");
	const [menuItem, setMenuItem] = useState(-1);
	const [showGetLocation, setShowGetLocation] = useState(false);
	const [address, setAddress] = useState({});

	useEffect(() => {
		console.log("useEffect App")
		let ca = 0;
		for (const item in cart) {
			ca += cart[item].quantity;
		}
		setCartAmount(ca)
	}, [cart, setCart, setCartAmount]);
	function servicesForm() {
		return (
			<BrowserRouter>
				<div className="outter mx-auto">
					<div className="row">
						<div className="col-sm-6">
							<Link to="/">
								<button className="removebutton align-text-bottom">
									<span className="logofont2">RivCo</span>
									<span className="logofont">DELIVERY</span>
								</button>
							</Link>
						</div>
						<div className="col-sm-6 text-sm-end text-xs-end ">
							<Link to="/cart">
								<button className="btn btn-secondary btn-lg m-1">
									<i className="bi bi-cart"></i> Cart ({cartAmount})
								</button>
							</Link>
						</div>
					</div>
					<div className="mx-auto p-3 blackborder">
						<Routes>
							<Route
								path='*'
								exact={true}
								element={
									<RestaurantsList
										setRestaurantName={setRestaurantName}
										roundedToFixed={roundedToFixed}
										showGetLocation={showGetLocation}
										setShowGetLocation={setShowGetLocation}
										address={address}
										setAddress={setAddress}
									/>
								}
							/>
							<Route
								path={"/orders"}
								element={
									<OrderList

									/>
								}
							/>
							<Route
								path={"/success"}
								element={
									<h1 className="text-center">Your order has been received!</h1>
								}
							/>
							<Route
								path={"/menu"}
								element={
									<ShowMenu
										restaurantName={restaurantName}
										restaurant={restaurant} 
									/>
								}
							/>
							<Route
								path={"/menu/item"}
								element={
									<ShowMenuItem
										cartAmount={cartAmount}
										setCartAmount={setCartAmount}
										restaurant={restaurant}
										restaurantName={restaurantName}
										menuItem={menuItem}
										cart={cart}
										setCart={setCart} />
								}
							/>
							<Route
								path={"/cart"}
								element={
									<Cart
										cartAmount={cartAmount}
										setCartAmount={setCartAmount}
										cart={cart}
										setCart={setCart} />
								} 
							/>
							<Route
								path={"/checkout"}
								element={
									<Checkout 
										cart={cart}
										setCart={setCart}
										showGetLocation={showGetLocation}
										setShowGetLocation={setShowGetLocation}
										address={address}
										setAddress={setAddress}
									/>
								} 
							/>
							<Route
								path={"/admin"}
								element={
									<Admin />
								}
							/>
							<Route
								path={"/admin2"}
								element={
									<Admin2 />
								}
							/>
						</Routes>
					</div>
					<Link to="/admin">
						Admin
					</Link>
					<span> </span>
					<Link to="/admin2">
						Admin2
					</Link>
					<span> </span>
					<Link to="/orders">
						Orders
					</Link>
				</div>
			</BrowserRouter>
		);
	}
	return (
		<div>
			<MenuContext.Provider value={
				[
					restaurant,
					setRestaurant,
					menuItem,
					setMenuItem
				]
			}>
				<br />
				<div>{servicesForm()}</div>

				<br />
			</MenuContext.Provider>
		</div>
	);
}
export default App;
