import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios';
import qs from 'qs';
import QuantitySelector from './QuantitySelector';

export default function ShowMenuItem(props) {
    const restaurantName = props.restaurantName;
    const cart = props.cart;
    const setCart = props.setCart;
    const [itemConfig, setItemConfig] = useState([]);
    const [itemIngredients, setItemIngredients] = useState([]);
    const [val1, setVal1] = useState(1);
    const [val2, setVal2] = useState(0);
    const [val3, setVal3] = useState(0);
    const [val4, setVal4] = useState(0);
    const [quantity, setQuantity] = useState(1);
    const navigate = useNavigate();
    const result = Object.groupBy(itemIngredients, i => i.type);
    const [enabled, setEnabled] = useState([false, false, false, 
        false, false, false, false, false, false, false, false, 
        false, false, false, false, false, false, false, false, 
        false, false, false, false, false, false, false, false, 
        false, false, false, false, false, false, false, false, ]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            setItemConfig(res.data);
        });
        axios.get('http://localhost:3000/api/menu/item/ingredients',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            setItemIngredients(res.data);
        });
    }, [cart, setCart]);

    function addToCart(e, key, item) {
        e.preventDefault();
        let boxes = [];
        let boxes2 = [];
        const form = e.target;
        const elements = form.elements['ingredients'];
        const customizer = form.elements['customizer'];
        const halfer = form.elements['halfer'];
        for (let i = 0; i < elements.length; i++) {
            const v = elements[i];
            boxes.push([v.checked, customizer[i].value]);  
            boxes2.push([halfer[i]]);
        }
        const cartItem = {
            name: item.name,
            size1: item.size1,
            val1: val1,
            size2: item.size2,
            val2: val2,
            size3: item.size3,
            val3: val3,
            size4: item.size4,
            val4: val4,
            ingredients: boxes,
            arrs: ingredientsData,
            quantity: quantity,
            price: item.price,
            customizer: customizer,
            halfer: boxes2,
        };
        //console.log("?? "+halfer)
        console.log(cartItem)
        /*const url = "http://localhost:3000/api/submitOrder";
        const options = {
            method: 'POST',
            headers: {
                'content-type':
                    'application/x-www-form-urlencoded'
            },
            data: qs.stringify(cartItem),
            url,
        };
        axios(options);*/
        cart.push(cartItem);
        setCart(
            cart.map((m) => {
                return m
            })
        );
        navigate("/showMenu");
    }

    function changeRadio1() {
        setVal1(1);
        setVal2(0);
        setVal3(0);
        setVal4(0);
    }

    function changeRadio2() {
        setVal1(0);
        setVal2(1);
        setVal3(0);
        setVal4(0);
    }

    function changeRadio3() {
        setVal1(0);
        setVal2(0);
        setVal3(1);
        setVal4(0);
    }
    function changeRadio4() {
        setVal1(0);
        setVal2(0);
        setVal3(0);
        setVal4(1);
    }

    const ingredientsData = [];
    function populateIngredientData() {
        for (const j in result) {
            for (const i in result[j]) {
                ingredientsData.push(result[j][i]);
            }
        }
    }

    let lastCategory = "";
    function setLastCategoryPrinted(v) {
        lastCategory = v;
    }

    populateIngredientData();
    return (
    <div>
        <Link to="/showMenu">
            <button className="btn btn-secondary btn-lg m-1">
                <i className="bi bi-arrow-return-left"></i> {restaurantName != undefined ? restaurantName : "Back"}
            </button>
        </Link>
        {
           itemConfig.map((item, key) =>
                <form
                    key={key}
                    className="form-check"
                    onSubmit={(e) => addToCart(e, key, item)}>
                    <div className="row">
                        <div className="col-sm-12 text-center">
                            <h2>{item.name}</h2>
                            <h3>
                                <b className="text-success">${item.price}</b>
                            </h3>
                            <QuantitySelector
                                inputValue={quantity}
                                onInputValueChange={setQuantity} />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-6">
                            <div>
                                <h5 className="indent">Size</h5>
                                {(item.size1 != null && item.size1 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                required
                                                type="radio"
                                                checked={val1}
                                                onChange={(e) => changeRadio1()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size1}
                                        </label>
                                    </div> : ""
                                }
                                {(item.size2 != null && item.size2 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                type="radio"
                                                checked={val2}
                                                onChange={(e) => changeRadio2()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size2}
                                        </label>
                                    </div> : ""
                                }
                                {(item.size3 != null && item.size3 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                type="radio"
                                                checked={val3}
                                                onChange={(e) => changeRadio3()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size3}
                                        </label>
                                    </div> : ""
                                }
                                {(item.size4 != null && item.size4 != "") ?
                                    <div className="p-3 m-1 blackborder">
                                        <label>
                                            <input
                                                type="radio"
                                                checked={val4}
                                                onChange={(e) => changeRadio4()}
                                                name="itemSize" />
                                            <span> </span>
                                            {item.size4}
                                        </label>
                                    </div> : ""
                                }
                            </div>
                        </div>
                        <div className="col-md-6">
                            {
                                ingredientsData.map((q, key) => {
                                    function changeEnabled(e) {
                                        enabled[key] = !enabled[key];
                                        console.log(key + " " + enabled[key]);
                                        setEnabled(enabled.map(e => e))
                                    }
                                    return (
                                        <div>
                                            {
                                                (lastCategory != q['type'] ? 
                                                    <h5 className="indent">{q['type']}</h5> : ""
                                                )
                                            }
                                            {
                                                setLastCategoryPrinted(q['type'])
                                            }
                                            <div className="p-3 m-1 blackborder">
                                                <div className="row">
                                                    <div className="col-lg-6">
                                                        {
                                                            (q['inputType'] == 0 ? 
                                                                (<label>
                                                                    <input
                                                                        onChange={(e) => changeEnabled(e)}
                                                                        type="checkbox"
                                                                        id="ingredients"
                                                                        name={q['type']} />
                                                                    <span> </span>
                                                                    <span>{q['ingredients_name']}</span>
                                                                </label>)
                                                                : 
                                                                (<label>
                                                                    <input
                                                                        required
                                                                        onChange={(e) => changeEnabled(e)}
                                                                        type="radio"
                                                                        id="ingredients"
                                                                        name={q['type']} />
                                                                    <span> </span>
                                                                    <span>{q['ingredients_name']}</span>
                                                                </label>)
                                                            )
                                                        }
                                                    </div>
                                                    <div className="col-lg-6 text-end">
                                                        {
                                                            <div>
                                                                <select 
                                                                        disabled={enabled[key] == false ? "disabled" : null}
                                                                        style={{visibility: q['customize'] == 0 ? "hidden" : "visible"}} 
                                                                        className="middle" 
                                                                        name="customizer" 
                                                                        id="customizer">
                                                                    <option value="Easy">Easy</option>
                                                                    <option value="Normal" selected>Normal</option>
                                                                    <option value="Extra">Extra</option>
                                                                </select>
                                                                <span> </span>
                                                                {q['halfable'] == 1 ?
                                                                    (<select 
                                                                            disabled={enabled[key] == false ? "disabled" : null}
                                                                            style={{visibility: q['customize'] == 0 ? "hidden" : "visible"}} 
                                                                            className="middle" 
                                                                            name="halfer" 
                                                                            id="halfer">
                                                                        <option value="Left Half">Left half</option>
                                                                        <option value="Whole" selected>Whole</option>
                                                                        <option value="Right Half">Right half</option>
                                                                    </select>) : ""
                                                                }
                                                            </div>
                                                        }
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    );
                                })
                            }
                            <input
                                type="submit"
                                className="btn btn-primary mt-1 form-control"
                                value={"Add " + quantity + " to cart"} />
                        </div>
                    </div>
                </form>
            )
        }
    </div>);
}
