import React, { useEffect, useState, useContext, useRef} from 'react';
import axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { MenuContext } from './App';


const apiKey = 'AIzaSyAyg7Hqr5wUTTv2uL6PMA3-F7fcHBGzhOw';
const mapApiJs = 'https://maps.googleapis.com/maps/api/js';
const geocodeJson = 'https://maps.googleapis.com/maps/api/geocode/json';

function loadAsyncScript(src) {
    return new Promise(resolve => {
        const script = document.createElement("script");
        Object.assign(script, {
            type: "text/javascript",
            async: true,
            src
        })
        script.addEventListener("load", () => resolve(script));
        document.head.appendChild(script);
    })
}
const extractAddress = (place) => {
    const address = {
        streetNumber: "",
        street: "",
        city: "",
        state: "",
        zip: "",
        plain() {
            const streetNumber = this.streetNumber ? this.streetNumber + ", " : "";
            const street = this.street ? this.street + ", " : "";
            const city = this.city ? this.city + ", " : "";
            const zip = this.zip ? this.zip + ", " : "";
            const state = this.state ? this.state + ", " : "";
            return streetNumber + street + city + zip + state;
        }
    }
    if (!Array.isArray(place?.address_components)) {
        return address;
    }
    place.address_components.forEach(component => {
        const types = component.types;
        const value = component.long_name;
        console.log(component);
        if (types.includes("street_number")) {
            address.streetNumber = value;
        }
        if (types.includes("route")) {
            address.street = value;
        }
        if (types.includes("locality")) {
            address.city = value;
        }
        if (types.includes("administrative_area_level_1")) {
            address.state = value;
        }
        if (types.includes("postal_code")) {
            address.zip = value;
        }
    });
    return address;
}
const defaultAddress = function () {
    return <span className='text-danger'><u>Address Required!</u></span>
};

export default function RestaurantsList(props) {
    const [restaurant, setRestaurant] = useContext(MenuContext);
    const [restaurants, setRestaurants] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/restaurants')
            .then(res => {
                console.log(res.data);
                setRestaurants(res.data);
            });
        initMapScript().then(() => initAutocomplete())
    }, []);
    function haversine_distance(lat, lng) {
        var R = 3958.8;
        var rlat1 = latitude * (Math.PI / 180);
        var rlat2 = lat * (Math.PI / 180);
        var difflat = rlat2 - rlat1;
        var difflon = (lng - longitude) * (Math.PI / 180);
        var d = 2 * R * Math.asin(Math.sqrt(Math.sin(difflat / 2) * Math.sin(difflat / 2) + Math.cos(rlat1) * Math.cos(rlat2) * Math.sin(difflon / 2) * Math.sin(difflon / 2)));
        return d;
    }
    const searchInput = useRef(null);
    const [address, setAddress] = useState({});
    const [latitude, setLatitude] = useState(-1);
    const [longitude, setLongitude] = useState(-1);
    const [distance, setDistance] = useState();
    const [debug, setDebug] = useState(true);

    const initMapScript = () => {
        if (window.google) {
            return Promise.resolve();
        }
        const src = `${mapApiJs}?key=${apiKey}&libraries=places,spherical,geometry`;
        return loadAsyncScript(src);
    }
    const onChangeAddress = (autocomplete) => {
        const place = autocomplete.getPlace();
        setAddress(extractAddress(place));
        console.log(place.geometry.location);
        setLatitude(place.geometry.location.lat());
        setLongitude(place.geometry.location.lng());
    }
    const initAutocomplete = () => {
        if (!searchInput.current) return;
        const autocomplete = new window.google.maps.places.Autocomplete(searchInput.current);
        const southwest = { lat: 33.833322851100824, lng: -117.46334029886367 };
        const northeast = { lat: 34.02489224499665, lng: -117.3135582245764 };
        const newBounds = new window.google.maps.LatLngBounds(southwest, northeast);
        autocomplete.setBounds(newBounds);
        autocomplete.setFields(["address_component", "geometry"]);
        autocomplete.addListener("place_changed", () => onChangeAddress(autocomplete));
    }
    function roundedToFixed(input, digits) {
        var rounder = Math.pow(10, digits);
        var result = (Math.round(input * rounder) / rounder).toFixed(digits);

        return result > 50 ? '--' : result;
    }
    function addressDisplayed() {
        return (
            <div className="address">
                <small>
                    Street: {address.streetNumber} {address.street}
                </small>
                <br />
                <small>City: {address.city}</small>
                <br />
                <small>State: {address.state}</small>
                <br />
                <small>Zip: {address.zip}</small>
            </div>
        );
    }
    return (
        <div>
            <div className="row search">
                <div className="col-sm-12">
                    <h6 className="m-1">Deliver to: {defaultAddress()}</h6>
                    <input
                        style={{ width: "90%" }}
                        className="m-1 mt-0 text-bg-dark text-info font-weight-bold"
                        ref={searchInput}
                        type="text"
                        placeholder="Search location...."
                    />
                    {debug ? addressDisplayed : ""}
                </div>
            </div>
            {restaurants.map((data, key) => {
                return (
                    <Link key={key} to={"/showMenu"}>
                        <button
                            className="btn btn-primary m-1"
                            onClick={(e) => setRestaurant(key + 1)}>
                            <div>
                                <b>{data.name}</b>
                                <br />
                                <small>{data.fee} Delivery Fee</small>
                                <br />
                                <small>
                                    {roundedToFixed(
                                        haversine_distance(data.lat, data.lng), 1)} Miles
                                </small>
                            </div>
                        </button>
                    </Link>
                );
            })}
        </div>
    );
}
