import React from 'react';
import axios from 'axios';

export class ShowMenuItem extends React.Component {
    constructor(props) {
        super();
        this.state = {
            itemConfig: [],
            itemSelected: false,
            menuItem: props.menuItem
        }
        axios.get('http://localhost:3000/api/menu/item', { params: { menuItem: props.menuItem } }).then(res => {
            console.log(res.data)
            this.setState({ itemConfig: res.data })
        })
    }
