import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from './App'
import QuantitySelector from './QuantitySelector';

export default function CartItem(props) {
    const calcSubtotal = props.calcSubtotal;
    const [cart, setCart] = useContext(CartContext);
    const cartItem = props.cartItem;
    const cid = props.cid;
    const [quantity, setQuantity] = useState(props.quantity);
    function recalc(e, cid) {
        setCart(removeFromCart(e, cid))
        calcSubtotal();
    }
    function removeFromCart(e, cid) {
        console.log(cart.length)
        //let newCart = cart.filter((cartItem, k) => k !== cid);
        let firstHalf = cart.slice(0, cid);
        let secondHalf = cart(3);
        let newCart = firstHalf.concat(secondHalf);

        console.log(newCart.length)
        setCart(newCart);
        console.log(cart.length)
        //};
        return newCart;
        //console.log(cart.length)
    }
    return (
        <div className="m-1">
            <h5>{cartItem.name}</h5>
            <b className="text-success">
                ${cartItem.price} x {quantity} = ${cartItem.price * quantity}
            </b>
            <br />
            <span>{cartItem.val1 == 1 ? cartItem.size1 : ""}</span>
            <span>{cartItem.val2 == 1 ? cartItem.size2 : ""}</span>
            <span>{cartItem.val3 == 1 ? cartItem.size3 : ""}</span>
            <span>{cartItem.val4 == 1 ? cartItem.size4 : ""}</span>
            <div className="row">
                <div className="col-sm-6">
                    <QuantitySelector
                        inputValue={quantity}
                        onInputValueChange={setQuantity} />
                </div>
                <div className="col-sm-6">
                    <button
                        className="btn btn-danger btn-sm"
                        onClick={(e) => recalc(e, cid)}>Remove</button>
                </div>
            </div>
        </div>
    )
}
