import React, { useEffect, useState, useContext } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom'
import axios from 'axios';
import qs from 'qs';
import QuantitySelector from './QuantitySelector';
import { CartContext } from './App'
/* eslint-disable */

export default function ShowMenuItem(props) {
    const restaurant = props.restaurant;
    const restaurantName = props.restaurantName;
    const cart = props.cart;
    const setCart = props.setCart;
    const [itemConfig, setItemConfig] = useState([]);
    const [itemIngredients, setItemIngredients] = useState([]);
    const [val1, setVal1] = useState(1);
    const [val2, setVal2] = useState(0);
    const [val3, setVal3] = useState(0);
    const [val4, setVal4] = useState(0);
    const [ingredient1, setIngredient1] = useState(1);
    const [ingredient2, setIngredient2] = useState(1);
    const [ingredient3, setIngredient3] = useState(1);
    const [ingredient4, setIngredient4] = useState(1);
    const [ingredient5, setIngredient5] = useState(1);
    const [ingredient6, setIngredient6] = useState(1);
    const [ingredient7, setIngredient7] = useState(1);
    const [ingredient8, setIngredient8] = useState(1);
    const [ingredients, setIngredients] = useState([]);
    const [type, setType] = useState("");
    const [quantity, setQuantity] = useState(1);
    const cartAmount = props.cartAmount;
    const setCartAmount = props.setCartAmount;
    const navigate = useNavigate();
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            console.log(res.data);
            setItemConfig(res.data);
        });

        axios.get('http://localhost:3000/api/menu/item/ingredients',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            console.log(res.data);
            setItemIngredients(res.data);
        });
    }, [cart, setCart]);
    function addToCart(e, key, item) {
        e.preventDefault();
        let boxes = [];
        const form = e.target;
        for (const g in form.elements['ingredients']) {
            const v = form.elements['ingredients'][g].checked;
            if (v || !v) {
                console.log(v); 
                boxes.push(v);
            }    
        }
        const cartItem = {

            name: item.name,
            size1: item.size1,
            val1: val1,
            size2: item.size2,
            val2: val2,
            size3: item.size3,
            val3: val3,
            size4: item.size4,
            val4: val4,
            ingredients: boxes,
            quantity: quantity,
            price: item.price,
        };
        console.log(cartItem)
        const url = "http://localhost:3000/api/submitOrder";
        const options = {
            method: 'POST',
            headers: {
                'content-type':
                    'application/x-www-form-urlencoded'
            },
            data: qs.stringify(cartItem),
            url,
        };
        axios(options);
        console.log("item added to cart " + item.name);
        cart.push(cartItem);
        setCart(
            cart.map((m) => {
                return m
            })
        );
        navigate("/showMenu");
    }
    function changeRadio1() {
        setVal1(1);
        setVal2(0);
        setVal3(0);
        setVal4(0);
    }

    function changeRadio2() {
        setVal1(0);
        setVal2(1);
        setVal3(0);
        setVal4(0);
    }

    function changeRadio3() {
        setVal1(0);
        setVal2(0);
        setVal3(1);
        setVal4(0);
    }
    function changeRadio4() {
        setVal1(0);
        setVal2(0);
        setVal3(0);
        setVal4(1);
    }
    const arrs = [];
    const titles = [];
    function kjk() {
        console.log(JSON.stringify(result));
        for (const mm in result) {
            const ar = Array.from(Object.values(result[mm]));
            titles.push(mm);
            arrs.push(ar.map((v) => { 
                return  v;
            }));
        }
    }
    const result = Object.groupBy(itemIngredients, i => i.type);
    console.log(result);
    return (
    <div>
        <Link to="/showMenu">
            <button className="btn btn-secondary btn-lg m-1">
                <i className="bi bi-arrow-return-left"></i> {restaurantName != undefined ? restaurantName : "Back"}
            </button>
        </Link>
        {
           itemConfig.map((item, key) =>
                <form
                    key={key}
                    className="form-check"
                    onSubmit={(e) => addToCart(e, key, item)}>
                    <div className="row" key={key}>
                        <h2>{item.name}</h2>
                        <div className="col-sm-6">
                            <b className="text-success">${item.price}</b>
                            <QuantitySelector
                                inputValue={quantity}
                                onInputValueChange={setQuantity} />
                            <h5>Item Size: </h5>
                            {(item.size1 != null && item.size1 != "") ?
                                <label>
                                    <input
                                        required
                                        type="radio"
                                        checked={val1}
                                        onChange={(e) => changeRadio1()}
                                        name="itemSize"
