import React, { useEffect, useState, useContext } from 'react';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom'
import { CartContext } from './App'
import QuantitySelector from './QuantitySelector';
import CartItem from './CartItem';

export default function Cart(props) {
    const cart = props.cart;
    const setCart = props.setCart;//const [cart, setCart] = useContext(CartContext);
    const [subtotal, setSubtotal] = useState(0.00);
    const calcSubtotal = (c) => {
        let totalAmount = 0;
        for (const item in c) {
            console.log("lll "+ c[item].name)
            totalAmount += c[item].quantity * c[item].price;
        }
        console.log(totalAmount);
        setSubtotal(totalAmount);
        return totalAmount;
    };
    function removeFromCart(e, cid) {
        console.log(cart.length);
        let newCart = cart.filter((cartItem, k) => k !== cid);
        console.log("rm from cart - newCart: " + newCart.length);
        setCart(newCart);
        console.log("rm from cart - cart: " + cart.length);
        calcSubtotal(newCart);
    }
    useEffect(() => {
        let newSubtotal = calcSubtotal(cart);
        setSubtotal(newSubtotal);
    }, [cart]);
    function recalc(e, cid) {
        removeFromCart(e, cid);
        console.log("recalc - cart size: " + cart.length);
        //let newSubtotal = calcSubtotal();
        //setSubtotal(newSubtotal);
    }
    function decrement(e, cartItem, key) {
        cart[key].quantity = cart[key].quantity - 1;
        const cart2 = cart;
        setCart(cart2);
    }

    function increment(e, cartItem) {
        cartItem.quantity = cartItem.quantity + 1
    }
    function printCart(cart) {
        console.log("printCart length: " + cart.length + " ");
        return (cart.map((cartItem, key) => {
            console.log("cartItem quantity: " + cartItem.quantity)
            return (
                <div className="cartitem" key={key}>
                    { cart.length === 0 ? "Empty." : ""}
                    <div className="m-1">
                        <h5>{cartItem.name}</h5>
                        <b className="text-success">
                            ${cartItem.price} x {cartItem.quantity} = ${cartItem.price * cartItem.quantity}
                        </b>
                        <br />
                        <span>{cartItem.val1 == 1 ? cartItem.size1 : ""}</span>
                        <span>{cartItem.val2 == 1 ? cartItem.size2 : ""}</span>
                        <span>{cartItem.val3 == 1 ? cartItem.size3 : ""}</span>
                        <span>{cartItem.val4 == 1 ? cartItem.size4 : ""}</span>
                        <div className="row">
                            <div className="col-sm-6">
                                <span className="input-group mb-1">
                                    <button
                                        type="button"
                                        className="btn btn-primary btn-sm btn-outline-secondary text-white"
                                        onClick={(e) => decrement(e, cartItem, key)}>
                                        <i class="bi bi-dash-lg"></i>
                                    </button>
                                    <input
                                        className={"input-number text-center"}
                                        disabled="disabled"
                                        type="number"
                                        value={cartItem.quantity}
                                        size="2" />
                                    <button
                                        type="button"
                                        className="btn btn-primary btn-sm btn-outline-secondary text-white"
                                        onClick={(e) => increment(e, cartItem)}>
                                        <i class="bi bi-plus-lg"></i>
                                    </button>
                                </span>
                            </div>
                            <div className="col-sm-6 float-sm-right">
                                <button
                                    className="btn btn-danger btn-sm"
                                    onClick={(e) => recalc(e, key)}>Remove</button>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }))
    }
    return (
        <div className="m-1">
            <Link to="/showMenu">
                <button className="btn btn-secondary btn-lg">
                    <i className="bi bi-arrow-return-left"></i> Back
                </button>
            </Link>
            <h1>Shopping Cart</h1>
                <div className="row">
                    <div className="col-sm-8">
                        {printCart(cart)}
                    </div>
                    <div className="col-sm-4">
                        <h3>Subtotal: <b className="text-success">
                            ${subtotal}</b>
                        </h3>
                    </div>
                </div>
        </div>
    );
}
