import React, { useEffect, useState } from 'react';
import currency from 'currency.js';
import { printCartItemDetails, calcSubtotal, printSubtotal } from './Cart';

export default function Checkout() {
		const [fee, setFee] = useState(0.00);
		const [subtotal, setSubtotal] = useState(0.00);
		useEffect(() => {
			let newSubtotal = calcSubtotal(cart, setSubtotal);
			setSubtotal(newSubtotal);
		}, [cart]);
		return (
			<div>
				<h1>Checkout</h1>
				<div className="row">
					<div className="col-sm-7">
					{
						cart.map(ci => {
							return (
								<div className="cartitem p-3">
									{printCartItemDetails(ci)}	
								</div>
							)
						})
					}
					</div>
					<div className="col-sm-5">
						{printSubtotal(subtotal)}
						<h3 className="text-end">
							Service Fee: <b>${roundedToFixed(fee, 2)}</b>
						</h3>
						<h3 className="text-end">
							Total: <b>${roundedToFixed(currency(subtotal).add(currency(fee)).value, 2)}</b>
						</h3>
					</div>
				</div>
			</div>
		);
	}
