import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import ShowMenu from './ShowMenu';
import ShowMenuItem from './ShowMenuItem';
import RestaurantsList from './RestaurantsList';
import Cart from './Cart';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'

export const MenuContext = React.createContext();
export const CartContext = React.createContext();

const apiKey = 'AIzaSyAyg7Hqr5wUTTv2uL6PMA3-F7fcHBGzhOw';
const mapApiJs = 'https://maps.googleapis.com/maps/api/js';
const geocodeJson = 'https://maps.googleapis.com/maps/api/geocode/json';

function loadAsyncScript(src) {
	return new Promise(resolve => {
		const script = document.createElement("script");
		Object.assign(script, {
			type: "text/javascript",
			async: true,
			src
		})
		script.addEventListener("load", () => resolve(script));
		document.head.appendChild(script);
	})
}
const extractAddress = (place) => {
	const address = {
		streetNumber: "",
		street: "",
		city: "",
		state: "",
		zip: "",
		plain() {
			const streetNumber = this.streetNumber ? this.streetNumber + ", " : "";
			const street = this.street ? this.street + ", " : "";
			const city = this.city ? this.city + ", " : "";
			const zip = this.zip ? this.zip + ", " : "";
			const state = this.state ? this.state + ", " : "";
			return streetNumber + street + city + zip + state;
		}
	}
	if (!Array.isArray(place?.address_components)) {
		return address;
	}
	place.address_components.forEach(component => {
		const types = component.types;
		const value = component.long_name;
		console.log(component);
		if (types.includes("street_number")) {
			address.streetNumber = value;
		}
		if (types.includes("route")) {
			address.street = value;
		}
		if (types.includes("locality")) {
			address.city = value;
		}
		if (types.includes("administrative_area_level_1")) {
			address.state = value;
		}
		if (types.includes("postal_code")) {
			address.zip = value;
		}
	});
	return address;
}

export function App(props) {
	const [debug, setDebug] = useState(true);
	const defaultAddress = function () {
		return <span className='text-danger'>Address Required!</span>
	};
	const [showGetLocation, setShowGetLocation] = useState(true);
	const [cart, setCart] = useState([]);
	const [restaurant, setRestaurant] = useState(-1);
	const [menuItem, setMenuItem] = useState(-1);
	const searchInput = useRef(null);
	const [address, setAddress] = useState({});
	const [latitude, setLatitude] = useState();
	const [longitude, setLongitude] = useState();
	function servicesForm() {
		return (
			<BrowserRouter>
				<Routes>
					<Route
						path="/"
						element=
					{
						<RestaurantsList />
					} />
					<Route
						path={"/showMenu"}
						element=
					{
						<ShowMenu restaurant={restaurant} />
					} />
					<Route
						path={"/showMenuItem"}
						element=
					{
						<ShowMenuItem
							restaurant={restaurant}
							menuItem={menuItem} />
					} />
					<Route
						path={"/cart"}
						element={<Cart />} />
				</Routes>
			</BrowserRouter>
		);
	}
	const initMapScript = () => {
		if (window.google) {
			return Promise.resolve();
		}
		const src = `${mapApiJs}?key=${apiKey}&libraries=places&v=weekly`;
		return loadAsyncScript(src);
	}
	const onChangeAddress = (autocomplete) => {
		const place = autocomplete.getPlace();
		setAddress(extractAddress(place));

		var geocoder = new window.google.maps.Geocoder();
		var address = extractAddress(place);
		geocoder.geocode({ 'address': address }, function (results, status) {
			if (status == google.maps.GeocoderStatus.OK) {
				// do something with the geocoded result
				//
				latitude = results[0].geometry.location.latitude
				longitude = results[0].geometry.location.longitude
			}
		});
	}
	const initAutocomplete = () => {
		if (!searchInput.current) return;
		const autocomplete = new window.google.maps.places.Autocomplete(searchInput.current);
		const southwest = { lat: 33.833322851100824, lng: -117.46334029886367 };
		const northeast = { lat: 34.02489224499665, lng: -117.3135582245764 };
		const newBounds = new window.google.maps.LatLngBounds(southwest, northeast);
		autocomplete.setBounds(newBounds);
		autocomplete.setFields(["address_component", "geometry"]);
		autocomplete.addListener("place_changed", () => onChangeAddress(autocomplete));
	}
	const reverseGeocode = ({ latitude: lat, longitude: lng }) => {
		const url = `${geocodeJson}?key=${apiKey}&latlng=${lat},${lng}`;
		searchInput.current.value = "Getting your location...";
		fetch(url)
			.then(response => response.json())
			.then(location => {
				const place = location.results[0];
				const _address = extractAddress(place);
				setAddress(_address);
				searchInput.current.value = _address.plain();
			})
	}
	const findMyLocation = () => {
		if (navigator.geolocation) {
			navigator.geolocation.getCurrentPosition(position => {
				reverseGeocode(position.coords)
			})
		}
	}
	useEffect(() => {
		initMapScript().then(() => initAutocomplete())

	}, []);
	return (
		<div>
			<div className="search">
				<h6>Deliver to: {defaultAddress}</h6>
				<input
					style={{width: "350px"}}
					ref={searchInput}
					type="text"
					placeholder="Search location...." />
			</div>
			<div className="address">
				<small>Street Number: <span>{address.streetNumber}</span></small>
				<br />
				<small>Street: <span>{address.street}</span></small>
				<br />
				<small>City: <span>{address.city}</span></small>
				<br />
				<small>State: <span>{address.state}</span></small>
				<br />
				<small>Zip: <span>{address.zip}</span></small>
			</div>
			{debug ? latitude : ""}
			<br />
			{debug ? longitude : ""}
			<CartContext.Provider value={[cart, setCart]}>
				<MenuContext.Provider value={[restaurant, setRestaurant, menuItem, setMenuItem]}>
					<div>
						{servicesForm()}
					</div>
				</MenuContext.Provider>
			</CartContext.Provider>
		</div>
	);
}
export default App;
