import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from './App'
import QuantitySelector from './QuantitySelector';

export default function CartItem(props) {
    const [cart, setCart] = useContext(CartContext);
    const cartItem = props.cartItem;
    const uniqueItemId = cartItem.id; // Use a unique identifier for each item
    const [quantity, setQuantity] = useState(props.quantity);

    function removeFromCart(e) {
        setCart((prevCart) => {
            // Create a new array with the item removed
            const updatedCart = prevCart.filter((item) => item.id !== uniqueItemId);
            return updatedCart;
        });
    }

    return (
        <div className="m-1">
            <h5>{cartItem.name} (x {quantity})</h5>
            <span>{cartItem.val1 == 1 ? cartItem.size1 : ""}</span>
            <span>{cartItem.val2 == 1 ? cartItem.size2 : ""}</span>
            <span>{cartItem.val3 == 1 ? cartItem.size3 : ""}</span>
            <span>{cartItem.val4 == 1 ? cartItem.size4 : ""}</span>
            <div className="row">
                <div className="col-xs-6 col-sm-6">
                    <QuantitySelector
                        inputValue={quantity}
                        onInputValueChange={setQuantity}
                    />
                </div>
                <div className="col-xs-6 col-sm-6">
                    <button
                        className="btn btn-primary btn-sm"
                        onClick={removeFromCart}
                    >
                        Remove
                    </button>
                </div>
            </div>
        </div>
    );
}
