				}
			}.then(res => console.log(form))
			.catch(err => console.log(err.form))*/
		this.setState({ sent: false });
	}
	servicesForm() {
		return (
		<form action="/api/register" method="POST"
		 onSubmit={(e) => this.submit(e)}>
			<input name="services" type="checkbox" />
