import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ShowMenu from './ShowMenu';
import ShowMenuItem from './ShowMenuItem';
import RestaurantsList from './RestaurantsList';
import Cart from './Cart';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'

export const MenuContext = React.createContext();

export const CartContext = React.createContext();

export function App(props) {
	const [debug, setDebug] = useState(true);
	const [address, setAddress] = useState("Unknown");
	const [cart, setCart] = useState([]);
	const [restaurant, setRestaurant] = useState(-1);
	const [menuItem, setMenuItem] = useState(-1);
	function servicesForm() {
		return (

			<BrowserRouter>
				<div className="m-1">
					<h5>Deliver to: <i>{address}</i></h5>
					<button className="btn btn-primary btn-sm" onClick={(e) => getAddress()}>Get My Location</button>
				</div>
				<Routes>
					<Route path="/" element={<RestaurantsList />} />
					<Route path={"/showMenu"} element={<ShowMenu restaurant={restaurant} />} />
					<Route path={"/showMenuItem"} element={<ShowMenuItem restaurant={restaurant} menuItem={menuItem} />} />

					<Route path={"/cart"} element={<Cart />} />
				</Routes>
			</BrowserRouter>
		);
	}
	const [latitude, setLatitude] = useState();
	const [longitude, setLongitude] = useState();
	useEffect(() => {
		navigator.geolocation.getCurrentPosition((position) => {
			setLatitude(position.coords.latitude);
			setLongitude(position.coords.longitude);
		})

	})
	function getAddress() {
		fetch('https://maps.googleapis.com/maps/api/geocode/json?latlng=' + {latitude} + ',' + {longitude} + '&sensor=false&key=AIzaSyAyg7Hqr5wUTTv2uL6PMA3-F7fcHBGzhOw')
			.then(response => response.json())
			.then(data => setAddress(data.results[0].formatted_address))
			.catch(error => alert(error));
	}
	return (
		<>
			{debug ? latitude : ""}
			<br />
			{debug ? longitude : ""}
			<CartContext.Provider value={[cart, setCart]}>
				<MenuContext.Provider value={[restaurant, setRestaurant, menuItem, setMenuItem]}>
					<div className="mx-auto">
						{servicesForm()}
					</div>
				</MenuContext.Provider>
			</CartContext.Provider>
		</>
	);
}
export default App;
