import React, { useEffect, useState } from 'react';
import axios from 'axios';
import qs from 'qs';
import { BrowserRouter, Routes, Route, Link, useNavigate } from 'react-router-dom'
/* eslint-disable */

export default function ShowMenuItem(props) {
    const [itemConfig, setItemConfig] = useState([]);
    const [val1, setVal1] = useState(0);
    const [val2, setVal2] = useState(0);
    const [val3, setVal3] = useState(0);
    const navigate = useNavigate();
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu/item',
            { params: { menuItem: props.menuItem } })
        .then(res => {
            console.log(res.data);
            setItemConfig(res.data);
        });
    }, []);
    function submit(e, key) {
        e.preventDefault();
        console.log("submit2")
        const data = [val1, val2, val3];
        console.log(data)
        const url = "http://localhost:3000/api/submitOrder";
        const options = {
            method: 'POST',
            headers: { 'content-type': 'application/x-www-form-urlencoded' },
            data: qs.stringify(data),
            url,
        };
        axios(options);
        //this.setState({ restaurant: key + 1 });
        //setRestaurant(key + 1);
        navigate("/orderStatus");
    }
    function changeRadio1() {
        setVal1(1);
        setVal2(0);
        setVal3(0);


    }

    function changeRadio2() {
        setVal1(0);
        setVal2(1);
        setVal3(0);

    }

    function changeRadio3() {
        setVal1(0);
        setVal2(0);
        setVal3(1);
    }
    return (
    <div>
        <Link to="/showMenu">Back</Link>
        {
           itemConfig.map((m, key) =>
               <div key={key}>
                   <h3>{m.name}</h3>
                   <form
                       key={key}
                       className="form-check"
                       onSubmit={(e) => submit(e, key)}>
                       {m.size1 != "" ?
                           <label>
                               <input
                                   type="radio"
                                   checked={val1}
                                   onChange={(e) => (val1 == 0 ? setVal1('on') : setVal1(0))}
                                   name="itemSize"
                                   className="form-check-input" />
                               {m.size1}
                           </label> : ""
                       }
                       {m.size1 != "" ? <br /> : ""}
                       {m.size2 != "" ?
                           <label>
                               <input
                                   type="radio"
                                   checked={val2}
                                   onChange={(e) => (val2 == 0 ? setVal2('on') : setVal2(0))}
                                   name="itemSize"
                                   className="form-check-input" />
                               {m.size2}
                           </label> : ""
                       }
                       {m.size1 != "" ? <br /> : ""}
                       {m.size3 != "" ?
                           <label>
                               <input
                                   type="radio"
                                   checked={val3}
                                   onChange={(e) => (val3 == 0 ? setVal3('on') : setVal3(0))}
                                   name="itemSize"
                                   className="form-check-input" />
                               {m.size3}
                           </label> : ""
                       }
                       {m.size1 != "" ? <br /> : ""}
                       <input
                           type="submit"
                           className="btn btn-primary"
                           value="Add to cart" />
                    </form>
                   <p>hi</p>
                </div>
            )
        }
    </div>);
}
