import React from 'react';
import axios from 'axios';
import qs from 'qs';
import ShowMenu from './ShowMenu';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'

export class App extends React.Component {
	constructor() {
		super();
		this.state = {
			sent: true,
			val1: null,
			val2: null,
			val3: null,
			restaurant: 1,
		};
	}
	render() {
		return (this.servicesForm());
	}
	submit = (key) => {
		/*e.preventDefault();
		console.log("submit2")
		const data = [this.state.val1, this.state.val2, this.state.val3];
		console.log(data)
		const url = "http://localhost:3000/api/register";
		const options = {
			method: 'POST',
			headers: { 'content-type': 'application/x-www-form-urlencoded' },
			data: qs.stringify(data),
			url,
		};
		axios(options);*/
		this.setState({ sent: false });
		this.setState({ restaurant: key + 1 });
	}
	servicesForm() {
		const restaurants = [{
			name: "Burger King",
			fee: "$0.50",
			miles: 2,
		},
		{
			name: "Taco Bell",
			fee: "$0.80",
			miles: 7,
		},
		{
			name: "Dominos",
			fee: "$0.20",
			miles: 1,
		}];
		return (
		<BrowserRouter>
			<div>
			{
				restaurants.map((data, key) => {
					return (
						<button className="btn btn-primary m-1" onClick={(e) => this.submit(key)}>
							<div>
								<h6>{data.name}</h6>
								<br />
								<small>{data.fee} Delivery Fee</small>
								<br />
								<small>{data.miles} Miles</small>
							</div>
						</button>
					);
				})
			}
			</div>
			<Routes>
				<Route path={"/showMenu"} element={<ShowMenu restaurant={this.state.restaurant} />} />
			</Routes>
			</BrowserRouter>
		)
	}
}
export default App;
