import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from './App'
import QuantitySelector from './QuantitySelector';

export default function Cart(props) {
    const [cart, setCart] = useContext(CartContext);

    function removeFromCart(e, key) {
        setCart(cart.filter(function (cartItem, k) {
            return k !== key;
        }));
    }
    return (
        <div>
        {
            cart.map((cartItem, key) =>
                <div key={key}>
                    <h5>{cartItem.name}</h5>
                    <small>{cartItem.val1 == 1 ? cartItem.size1 : ""}</small>
                    <small>{cartItem.val2 == 1 ? cartItem.size2 : ""}</small>
                    <small>{cartItem.val3 == 1 ? cartItem.size3 : ""}</small>
                    <QuantitySelector
                        inputValue={1} />
                    <button
                        className="btn btn-primary btn-sm"
                        onClick={(e) => removeFromCart(e, key)}>Remove</button>
                </div>
            )
        }
        </div>
    );
}
