import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import qs from 'qs';

export class App extends React.Component {
	constructor() {
		super();
		this.state = {
			sent: true,
			val1: null,
			val2: null,
			val3: null,
		};
	}
	render() {
		return (this.state.sent ? this.servicesForm() : this.b());
	}
	submit(e) {
		e.preventDefault();
		console.log("submit2")
		const data = [this.state.val1, this.state.val2, this.state.val3];
		console.log(data)
		const url = "http://localhost:3000/api/register";
		const options = {
			method: 'POST',
			headers: { 'content-type': 'application/x-www-form-urlencoded' },
			data: qs.stringify(data),
			url,
		};
		axios(options);
		this.setState({ sent: false });
	}
	servicesForm() {
		return (
		<form action="/api/register" method="POST"
		 onSubmit={(e) => this.submit(e)}>
				<input name="services" onChange={(e) => this.setState({ val1: e.target.value })} type="checkbox" />
			<label>Service #1</label>
			<br />
			<input name="services" onChange={(e) => this.setState({ val2: e.target.value })} type="checkbox" />
            <label>Service #2</label>
            <br />
			<input name="services" onChange={(e) => this.setState({ val3: e.target.value })} type="checkbox" />
            <label>Service #3</label>
			<br />
			<input className="btn btn-primary" type="submit"  />
			<br />
		</form>);
	}
	b() {
		return <div>Service request submitted.</div>;
	}
}
export default App;
