import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from './App'
import QuantitySelector from './QuantitySelector';


export default function CartItem(props) {
    const cartItem = props.cartItem;
    const key = props.key;
    const [quantity, setQuantity] = useState(props.quantity);
    const [cart, setCart] = useContext(CartContext);
    function removeFromCart(e, key) {
        setCart(cart.filter(function (cartItem, k) {
            return k !== key;
        }));
    }
    return (
        <div className="m-1" key={key}>
            <h5>{cartItem.name} (x {cartItem.quantity})</h5>
            <>{cartItem.val1 == 1 ? cartItem.size1 : ""}</>
            <>{cartItem.val2 == 1 ? cartItem.size2 : ""}</>
            <>{cartItem.val3 == 1 ? cartItem.size3 : ""}</>
            <>{cartItem.val4 == 1 ? cartItem.size4 : ""}</>
            <div className="row">
                <div class="col-xs-6 col-sm-6">
                    <QuantitySelector
                        onLoad={setQuantity(cartItem.quantity)}
                        inputValue={quantity}
                        onInputValueChange={setQuantity}
                    />
                </div>
                <div class="col-xs-6 col-sm-6">
                    <button
                        className="btn btn-primary btn-sm"
                        onClick={(e) => removeFromCart(e, key)}>Remove</button>
                </div>
            </div>
        </div>
    )
}
