import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import ShowMenuItem from './ShowMenuItem';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { MenuContext } from './App'

export default function ShowMenu(props) {
    const [restaurant, setRestaurant, menuItem, setMenuItem] = useContext(MenuContext);
    const [menu, setMenu] = useState([]);
    const restaurantName = props.restaurantName;
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu', { params: { restaurant: props.restaurant } }).then(res => {
            console.log(res.data);
            setMenu(res.data);
        });
    }, []);
    function changeMenuItem(m) {
        setMenuItem(m.id);
        console.log("menu = " + menuItem);
    }
    return (
        <div>
            <Link to="/">
                <button className="btn btn-secondary btn-lg m-1">
                    <i className="bi bi-arrow-return-left"></i> {restaurantName == undefined ? restaurantName : "Back"}
                </button>
            </Link>
            <Link to="/cart">
                <button className="btn btn-secondary btn-lg m-1">
                    <i className="bi bi-cart"></i> Cart
                    </button>
            </Link>
            <br />
            {
                menu.map((m, key) =>
                    <Link key={key} to="/showMenuItem">
                        <button
                            className="btn btn-primary m-1"
                            onClick={(e) => changeMenuItem(m)}>
                            {m.name}
                        </button>
                    </Link>
                )
            }
        </div>
    )
}
