import React, { useEffect, useState } from 'react';

export default function QuantitySelector(props) {
    const [quantity, setQuantity] = useState(0);
    function increment() {
        quantity < 100 ? setQuantity(quantity + 1) : setQuantity(100);
    }
    function decrement() {
        quantity > 0 ? setQuantity(quantity - 1) : setQuantity(0);
    }
    return (
        <div className="input-group">
            <button className="btn btn-success btn-sm" onClick={(e) => increment()}>+</button>
            <input disabled="disabled" type="text" value={quantity} max-width="100px" />
            <button className="btn btn-danger btn-sm" onClick={(e) => decrement()}><span class="fa fa-minus"></span></button>
        </div>
    );
}
