import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ShowMenuItem from './ShowMenuItem';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { MenuContext } from './App'

export default function ShowMenu(props) {
    const [restaurant, setRestaurant] = useContext(MenuContext);
    const [menu, setMenu] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/menu', { params: { restaurant: props.restaurant } }).then(res => {
            console.log(res.data);
            setMenu(res.data);
        });
    }, []);

    return (
        <div>
            <Link to="/">Back</Link><br />
            {
                menu.map((m, key) =>
                    <Link key={key} to={"/showMenuItem/" +  restaurant}>
                        <button className="btn btn-primary m-1">{m.name}</button>
                    </Link>
                )
            }
        </div>
    )
}
