import React, { useEffect } from 'react';
import axios from 'axios';
import { getMenuItemConfig } from '../../uber_server/database';

export class ShowMenuItem extends React.Component {
    constructor(props) {
        super();
        this.state = {
            menuItem: props.menuItem,
            itemConfig: null,
        }
        useEffect(() =>
            axios.get('http://localhost:3000/api/menu/item', { params: { menuItem: props.menuItem } }).then(res => {
                console.log(res.data)
                this.setState({ itemConfig: res });
            }), []);
    }
    render() {
        return (
            <div>{this.state.itemConfig.map((m, key) =>
                <button className="btn btn-primary m-1">
                    <p>{m.size1}</p>
                </button>)}
            </div>)
    }
}
