import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom'
import { MenuContext } from './App'
/* eslint-disable */

export default function RestaurantsList(props) {
    const [restaurant, setRestaurant] = useContext(MenuContext);
    const [restaurants, setRestaurants] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:3000/api/restaurants')
            .then(res => {
                console.log(res.data);
                setRestaurants(res.data);
            });
       /* const r = [{
            name: "Burger King",
            fee: "$0.50",
            miles: 2,
        },
        {
            name: "Taco Bell",
            fee: "$0.80",
            miles: 7,
        },
        {
            name: "Dominos",
            fee: "$0.20",
            miles: 1,
        }];*/
        //setRestaurants(r);
    }, []);

    return (
        <div>
            {restaurants.map((data, key) => {
                return (
                    <Link key={key} to={"/showMenu"}>
                        <button className="btn btn-primary m-1" onClick={(e) => setRestaurant(key + 1)}>
                            <div>
                                <h6>{data.name}</h6>
                                <br />
                                <small>{data.fee} Delivery Fee</small>
                                <br />
                                <small>{data.distance} Miles</small>
                            </div>
                        </button>
                    </Link>
                );
            })}
        </div>
    );
}
