import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

export class App extends React.Component {
	constructor() {
		super();
		this.state = {
			sent: true,

		};
	}

	render() {
		return (this.state.sent ? this.servicesForm() : this.b());
	}

	submit(e) {
		e.preventDefault();
		const data = Array.from(e.target.elements)
			.filter((input) => input.name)
			.reduce((obj, input) => Object.assign(obj, { [input.name]: input.value }), {});
		const url = "http://localhost:3000/api/register";
		axios.post(url, data)
   				.then(res => console.log(data))
   				.catch(err => console.log(err.data))
		this.setState({sent: false});
	}

	servicesForm() {
		return (
		<form action="/results" name="services" method="POST">
			<input name="service1" type="checkbox" />
			<label>Service #1</label>
			<br />
			<input name="service2" type="checkbox" />
            <label>Service #2</label>
            <br />
			<input name="service3" type="checkbox" />
            <label>Service #3</label>
            <br />
			<input className="btn btn-primary" type="submit" onClick={(e)=>this.submit(e)} />
			<br />
		</form>);
	}

	b() {
		return <div>Service request submitted.</div>;
	}
}

export default App;
