import React, { useEffect, useState } from 'react';

export default function QuantitySelector(props) {
    const [quantity, setQuantity] = useState(1);
    function increment(e) {
        quantity < 100 ? setQuantity(quantity + 1) : setQuantity(100);
        props.onInputValueChange(e.target.value)
    }
    function decrement(e) {
        quantity > 1 ? setQuantity(quantity - 1) : setQuantity(1);
        props.onInputValueChange(e.target.value)
    }
    return (
        <div className="input-group text-center">
            <button type="button"
                className="btn btn-danger btn-sm btn-outline-secondary"
                onClick={(e) => decrement(e)}>-</button>
            <input
                className={"input-number"}
                disabled="disabled"
                type="text"
                value={quantity}
                size="2"
                min="1"
                max="10" />
            <button type="button"
                className="btn btn-success btn-sm btn-outline-secondary"
                onClick={(e) => increment(e)}>+</button>

        </div>
    );
}
